--

CREATE FUNCTION public.accept_invitation(character varying[]) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
DECLARE

	_dato		 	ALIAS FOR $1;

	_user_id		INTEGER;
	_invitation_id		INTEGER;

	_row_rol		RECORD;
	_row_inv		RECORD;
	_row_user		RECORD;
	_reg_shadow		RECORD;


BEGIN
	_user_id		= _dato[1];
	_invitation_id		= _dato[2];



	IF NOT EXISTS(SELECT * FROM public.users WHERE id = _user_id)THEN
		RETURN 2;--No existe el usuario logueado
	END IF;
	IF NOT EXISTS(SELECT * FROM public.invitations WHERE id = _invitation_id)THEN
		RETURN 3;--No existe la invitación que desea aceptar
	END IF;

	SELECT business_id, code, aditional_param, from_user_id, to_user_id, shadow_id,to_email INTO _row_inv
	FROM notifications.view_all_invitations WHERE invitation_id =  _invitation_id;


	--Se obtiene el usuario de la empresa
	SELECT ub.user_id INTO _row_user FROM public.users_business ub
	JOIN public.user_roles ur ON(ur.id = ub.user_role_id)
	WHERE role_code = 'PAD' AND business_id = _row_inv.business_id;


	CASE _row_inv.code
	WHEN 'INVCLI' THEN

		IF NOT EXISTS (SELECT 1
				FROM public.clients_business cb
				WHERE user_id = _user_id AND  business_id = _row_inv.business_id) THEN

			INSERT INTO public.clients_business(
				user_id,
				business_id
			) VALUES (
				_user_id,
				_row_inv.business_id
			);

			--Si la cita se encuentra vigente, se crea la notificación hacia el cliente, informando que el negocio creó una cita
			IF EXISTS (SELECT 1 FROM shadow_appointments WHERE id = _row_inv.shadow_id AND NOW()::timestamp < time_start::timestamp) THEN
				PERFORM save_invitation_notification(_invitation_id,1);
			END IF;

			-- se crea una invitación hacia el usuario del negocio para notificarle que el usuario se ha hecho cliente.
			INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
			VALUES (_user_id, _row_user.user_id, _row_inv.shadow_id, TRUE, 9);

			_invitation_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;
			PERFORM save_invitation_notification(_invitation_id,8);

		END IF;

	WHEN 'INVSOC' THEN

		IF NOT EXISTS ( SELECT 1
				FROM public.users_business ub
				WHERE user_id = _user_id
				AND business_id = _row_inv.business_id
				AND user_role_id != (SELECT id FROM public.user_roles WHERE role_code = 'PAD')) THEN

			SELECT id AS user_role_id INTO _row_rol FROM public.user_roles WHERE role_code = _row_inv.aditional_param->>'role_code'::text;

			INSERT INTO public.users_business(
				user_id,
				business_id,
				user_role_id
			) VALUES (
				_user_id,
				_row_inv.business_id,
				_row_rol.user_role_id
			);
		END IF;

	WHEN 'INVAMI' THEN

		IF NOT EXISTS (SELECT *
				FROM public.friends f
				WHERE from_user_id = _row_inv.from_user_id AND to_user_id = _row_inv.to_user_id) THEN

			INSERT INTO public.friends(
				from_user_id,
				to_user_id
			) VALUES (
				_row_inv.from_user_id,
				_row_inv.to_user_id
			);

			PERFORM save_invitation_notification(_invitation_id,4);
		END IF;
	ELSE
		RETURN 0; --Ninguno
	END CASE;

	RETURN 1;--se registro satisfactoriamente


END;
$_$;


