-- Name: cancel_appointment(integer, json, json); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.cancel_appointment(p_user_id integer, json_configuracion json, json_data json) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE

	return_record   	t_appointment%rowtype;
	_shadow			shadow_appointments%rowtype;
	_json_configuracion	json := json_configuracion;
	_json_data		json := json_data;
	_id_shadow		INTEGER;

	_json			RECORD;

BEGIN

	_id_shadow := _json_data::json->>'id_shadow';

	-- verificando si el usuario que esta eliminado la informacion es realmente usuario del sistema
	IF NOT EXISTS(
		SELECT sa.ID FROM shadow_appointments sa
		JOIN services s ON s.id = sa.service_id
		JOIN users_business ub ON s.busines_id = ub.business_id
		WHERE ub.user_id = p_user_id AND sa.id = _id_shadow
	) THEN
		RETURN 2;
	END IF;

	-- Validando si existe una invitacion pagada asociada a la cita.
	-- SELECT * FROM invitations i  WHERE i.shadow_id = 1733 AND paid_out_clients_id IS NOT NULL
	IF EXISTS(SELECT * FROM invitations i WHERE i.shadow_id = _id_shadow AND paid_out_clients_id IS NOT NULL limit 1)THEN
		RETURN 3;-- No puedes eliminar la cita porque tienes invitaciones pagadas
	END IF;

	CASE _json_configuracion::json->>'tipo'

	WHEN 'dia' THEN
		DELETE FROM invitations WHERE shadow_id = _id_shadow;
		DELETE FROM shadow_appointments WHERE id = _id_shadow;
		RETURN 1;
	END CASE;

	RETURN 0;
END;
$$;


