--
-- Name: confirm_pay(character varying[]); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.confirm_pay(character varying[]) RETURNS json
    LANGUAGE plpgsql
    AS $_$

DECLARE



	_dato				ALIAS FOR $1;

	p_user_id			INTEGER;

	p_shadow_id			INTEGER;

	p_price				DOUBLE PRECISION;

	p_emails_users_give_away	CHARACTER VARYING;

	p_is_alone_gift			BOOLEAN;



	_quantity			INTEGER;

	_quantity_to_give_away		INTEGER;

	_quantity_shadow_paid		INTEGER;

	_limit_persons 			INTEGER;

	_space_available 		INTEGER;



	_users_give_away_id		INTEGER;

	_result_json			JSON;

	_reg_shadow			RECORD;

	_reg_user			RECORD;

	_reg				RECORD;

	_spa_tim			RECORD;

	_arr_emails			CHARACTER VARYING[];





BEGIN



	p_user_id			= _dato[1];

	p_shadow_id			= _dato[2];

	p_price				= _dato[3];

	p_emails_users_give_away	= _dato[4];

	p_is_alone_gift			= _dato[5];





	--Se obtiene las fechas de la cita a comprar, para validar si el que esta comprando o el usuario a quien se le regala la cita tiene otra cita que interfiera con el horario

	SELECT time_start, time_end, service_id, is_scheduled INTO _reg_shadow

	FROM shadow_appointments sa

	JOIN services s ON (s.id = sa.service_id)

	WHERE sa.id = p_shadow_id;

	--se obtiene el email del usuario logueado

	SELECT email INTO _reg_user FROM users u WHERE u.id = p_user_id;



	_arr_emails = string_to_array(p_emails_users_give_away,',');





	--Cantidad de citas a regalar

	_quantity_to_give_away = coalesce(array_length(_arr_emails,1),'0');

	IF(_quantity_to_give_away > 0)THEN

		_quantity = _quantity_to_give_away + 1;

	ELSE

		_quantity = 1;

	END IF;



	IF NOT EXISTS ( SELECT 1 FROM shadow_appointments WHERE id = p_shadow_id) THEN

		_result_json = json_build_object(

			'value',10,

			'message','Disculpa, no existe la cita.'

		);

		RETURN _result_json; -- no es acto para registrar, no existe la cita.

	END IF;





	-- verificando si la cita esta vigente y se le da una holgura antes del inicio de la cita deacuerdo a la configuracion definida

	SELECT value INTO _spa_tim FROM configuration_data WHERE code = 'spa_tim';

	IF NOT EXISTS ( SELECT 1 FROM shadow_appointments WHERE id = p_shadow_id AND NOW() + _spa_tim.value::interval < time_start) THEN

		_result_json = json_build_object(

			'value',2,

			'message','Disculpa, ya la cita no se encuentra vigente, para que puedas comprarla tiene que comprar 1 hora antes del inicio de la cita.'

		);

		RETURN _result_json; -- no es acto para registrar, la cita ya se encuentra vencida.

	END IF;



	-- si es diferente a regalar cita exclusivamente se valida lo siguiente

	IF(p_is_alone_gift IS FALSE)THEN

		-- verificando si ya el usuario logueado a comprado el servicio

		--SELECT * FROM invitations i WHERE  i.paid_out_clients_id IS NOT NULL AND i.shadow_id = 1702 AND i.to_user_id = 190

		IF EXISTS ( SELECT 1 FROM invitations i WHERE  i.paid_out_clients_id IS NOT NULL AND i.shadow_id = p_shadow_id AND i.to_user_id = p_user_id) THEN

			_result_json = json_build_object(

				'value',6,

				'message','Ups!, ya has comprado y apartado la cita, no puedes volver a comprar la misma cita'

			);

			RETURN _result_json; -- no es acto para registrar, ya el usuario a comprado este producto.

		END IF;



		--Validar si el usuario logueado que compra la cita  tiene otra cita que interfiera con el horario

		IF EXISTS(SELECT 1 FROM (

			SELECT  CASE WHEN to_email IS NULL THEN (SELECT email FROM users u WHERE u.id = i.to_user_id ) ELSE to_email END AS emails

			FROM invitations i

			JOIN shadow_appointments sa ON(sa.id = i.shadow_id)

			WHERE i.paid_out_clients_id IS NOT NULL

			AND (sa.time_start::timestamp,  sa.time_end::timestamp) OVERLAPS (_reg_shadow.time_start::timestamp, _reg_shadow.time_end::timestamp)

			) AS sq WHERE sq.emails = _reg_user.email)

		THEN

			_result_json = json_build_object(

				'value',7,

				'message','Disculpa, ya tienes una cita pautada que interfiere con el horario de ésta cita'

			);

			RETURN _result_json; -- ya tienes una cita pautada que interfiere con el horario de ésta cita.

		END IF;



	END IF;





	-- verificando si existen cupos disponibles

	_quantity_shadow_paid = (SELECT coalesce(SUM(quantity),0) FROM paid_out_clients WHERE shadow_id = p_shadow_id)::integer;

	_limit_persons = (SELECT limit_persons FROM shadow_appointments sa JOIN services s ON (sa.service_id =s.id) WHERE sa.id = p_shadow_id)::integer;

	IF (_quantity_shadow_paid >= _limit_persons) THEN

		_result_json = json_build_object(

			'value',3,

			'message','Disculpa, ya no hay cupos disponible para esta cita.'

		);

		RETURN _result_json;-- no es acto para registrar, ya no hay cupos disponible.

	ELSE

		_space_available = (_limit_persons - _quantity_shadow_paid);



		IF(_quantity > _space_available)THEN

			_result_json = json_build_object(

				'value',5,

				'message','Disculpa, estas superando la cantidad de cupos disponible para la cita, elimine algún(os) regalo(s) e intente de nuevo.'

			);

			RETURN _result_json;-- supera la cantidad de cupos disponible para la cita

		END IF;

	END IF;



	-- verificando si se ha modificado el precio del servicio

	IF NOT EXISTS(SELECT price

			FROM services s

			JOIN shadow_appointments sa ON(sa.service_id = s.id)

			WHERE sa.id = p_shadow_id AND s.price = p_price) THEN

		_result_json = json_build_object(

			'value',4,

			'message','Disculpa, el precio de este servicio ha cambiado, verifica el nuevo precio publicado.'

		);

		RETURN _result_json;-- no es acto para registrar, ya que se modificó el precio del servicio.

	END IF;





	--Validar si el usuario a quien se le regala la cita tiene otra cita que interfiera con el horario

	IF (p_emails_users_give_away != '')THEN



		--Se valida que el usuario que compra la cita no se regale a si mismo

		SELECT (_arr_emails @> ARRAY(SELECT email FROM users u WHERE id = p_user_id)) AS give_me INTO _reg;

		IF (_reg.give_me::boolean = true)THEN

			_result_json = json_build_object(

				'value',9,

				'message','Disculpa, no se puede regalar la cita que esta comprando.'

			);

			RETURN _result_json;

		END IF;



		SELECT array_to_string(array_agg(sq.emails), ','::text) AS emails_users_give_away INTO _reg

		FROM (  SELECT  CASE WHEN to_email IS NULL THEN (SELECT email FROM users u WHERE u.id = i.to_user_id ) ELSE to_email END AS emails

			FROM invitations i

			JOIN shadow_appointments sa ON(sa.id = i.shadow_id)

			WHERE i.paid_out_clients_id IS NOT NULL

			AND (sa.time_start::timestamp,  sa.time_end::timestamp) OVERLAPS (_reg_shadow.time_start::timestamp, _reg_shadow.time_end::timestamp)

			-- la siguiente condicion se debe mejorar, la logica que se debe acomplar es de la recien creada tabla notification.user_notificacion

			AND i.type_invitation NOT IN (9,10,11,12)

		) AS sq

		WHERE sq.emails = ANY(_arr_emails::text[]);



		RAISE NOTICE 'emails_users_give_away-->>%',_reg.emails_users_give_away;

		IF(_reg.emails_users_give_away IS NOT NULL)THEN



			_result_json = json_build_object(

				'value',8,

				'message','Disculpa, el o los usuario(s) ya tiene(n) otra cita que interfiere con el horario de ésta, regalale a otro e intente de nuevo',

				'additional_data',_reg.emails_users_give_away

			);

			RETURN _result_json; -- ya tienes una cita pautada que interfiere con el horario de ésta cita.

		END IF;

	END IF;



	--validando que la cita no sea pagada por el mismo dueño del negocio

	IF EXISTS(SELECT sa.id, sa.service_id, s.busines_id,ub.user_id

			FROM public.shadow_appointments sa

			JOIN public.services s ON (s.id = sa.service_id)

			JOIN public.users_business ub ON(ub.business_id = s.busines_id)

			JOIN public.user_roles ur ON(ur.id = user_role_id AND role_code = 'PAD')

			WHERE sa.id = p_shadow_id AND ub.user_id = p_user_id)THEN



		_result_json = json_build_object(

			'value',11,

			'message','Disculpa, no puedes comprar la cita porque eres el dueño de la empresa'

		);

		RETURN _result_json;



	END IF;





	_result_json = json_build_object(

		'value',1,

		'message','Acto para comprar la cita'

	);

	RETURN _result_json;



END;

$_$;


