-- Name: get_appointment_client(integer, integer, date, date); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.get_appointment_client(p_client_id integer, p_service_id integer, p_start date, p_end date) RETURNS SETOF public.t_appointment_client
    LANGUAGE plpgsql
    AS $$

DECLARE

	return_record   		t_appointment_client%rowtype;
	_appointment			RECORD;
	_color				RECORD;
	_color_paid			RECORD;
	_query				TEXT;

BEGIN

	-- color por defecto
	SELECT * into _color 		FROM appointment_colors WHERE code = 'invc';
	SELECT * into _color_paid 	FROM appointment_colors WHERE code = 'pinv';

	-- buscando solo aquellos registros pertenecientes al cliente del negocio
	_query := 'SELECT sa.* FROM shadow_appointments sa '
		|| 'JOIN services s ON sa.service_id = s.id '
		|| 'JOIN clients_business ub ON (ub.business_id = s.busines_id AND ub.user_id = '|| p_client_id ||') '
		|| 'WHERE '
		|| 's.id = '|| p_service_id ||' '
		|| 'AND (sa.time_start::date >= '''|| p_start ||''' AND sa.time_end <=  '''|| p_end ||''' OR sa.time_end IS NULL ) '
		|| 'AND sa.active = TRUE';


	raise notice '_query-->%', _query;
	raise notice 'p_start-->%', p_start;
	raise notice 'p_end-->%', p_end;

	RETURN  QUERY (
		SELECT	a.title::CHARACTER VARYING,
			a."start"::TIMESTAMP,
			a."end"::TIMESTAMP,
			a.active::INTEGER,
			a.id_shadow::INTEGER,
			a.id::CHARACTER VARYING,
			CASE WHEN i.paid_out_clients_id IS NULL THEN
				_color.color::CHARACTER VARYING
			ELSE
				_color_paid.color::CHARACTER VARYING
			END AS 	backgroundColor,
			CASE WHEN i.paid_out_clients_id IS NULL THEN
				_color.border_color::CHARACTER VARYING
			ELSE
				_color_paid.border_color::CHARACTER VARYING
			END AS 	borderColor,
			CASE WHEN i.paid_out_clients_id IS NULL THEN
				0::integer -- no pagado
			ELSE
				1 -- pagado
			END AS state
		FROM get_base_appointment(_query,p_start,p_end) as a
		LEFT JOIN invitations i ON(i.shadow_id = a.id_shadow and i.to_user_id= p_client_id AND i.paid_out_clients_id IS NOT NULL
		AND type_invitation IN(1, 5, 3, 4, 8))
	);
END;

$$;


