				_color_paid.border_color::CHARACTER VARYING
			END AS 	borderColor,

			CASE WHEN i.paid_out_clients_id IS NULL THEN
				0::integer -- no pagado
			ELSE
				1 -- pagado
			END AS state
		FROM get_base_appointment(_query,p_start,p_end) as a
		LEFT JOIN invitations i ON(i.shadow_id = a.id_shadow AND i.paid_out_clients_id IS NOT NULL
		AND type_invitation IN(1,6,7,8, 4) AND to_user_id = p_client_id)
	);
END;

