--
-- Name: get_availability_base(text, date, date); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.get_availability_base(_query text, p_start date, p_end date) RETURNS SETOF public.t_availability
    LANGUAGE plpgsql
    AS $$

DECLARE



	return_record   		t_availability%rowtype;

	_availability			availabilitys%rowtype;

	_generate_series		RECORD;

	_calendar			JSON;

	_start				date	:=	p_start;

	_end				date	:=	p_end;



	_recurrencia			character varying[];

	_crear_dia			boolean	:= FALSE;

	_sequence			character varying;



	-- queries

	_query_shadow_appointment	character varying;

	_query_business			character varying := '';



	_time_start			timestamp;

	_time_end			timestamp;



	_natural_time_start		timestamp;

	_natural_time_end		timestamp;



	_diferent_time			integer;





BEGIN



	raise notice '%',_query;



	-- por ahora siempre entrara 1 vez

	FOR _availability IN EXECUTE (

		_query

	) LOOP



		-- recorriendo disponibilidades del json

		FOR _calendar IN (SELECT VALUE FROM JSON_ARRAY_ELEMENTS(_availability.calendar::json)) LOOP

			IF ((_calendar->>'d')::BOOLEAN = FALSE) THEN

				_diferent_time := (_calendar->>'end')::date - (_calendar->>'start')::date;



				_natural_time_start := (_calendar->>'start')::TIMESTAMP;

				_natural_time_end := (_calendar->>'end')::TIMESTAMP;



				FOR _generate_series IN (SELECT generate_series::DATE AS time_dinamic FROM generate_series(p_start,p_end,'1 day')) LOOP



					IF(TO_CHAR(TO_DATE(_calendar::json->>'start','YYYY-MM-DD'),'d') = TO_CHAR(_generate_series.time_dinamic::DATE,'d'))THEN



						_time_start 	:= TO_TIMESTAMP(TO_CHAR(_generate_series.time_dinamic,'YYYY-MM-DD')

						||' '|| TO_CHAR(TO_TIMESTAMP(_calendar::json->>'start','YYYY-MM-DD HH24:mi.ss'),'HH24:mi.ss'),'YYYY-MM-DD HH24:mi.ss' );



						_time_end 	:= TO_TIMESTAMP(TO_CHAR(_generate_series.time_dinamic,'YYYY-MM-DD')

						||' '|| TO_CHAR(TO_TIMESTAMP(_calendar::json->>'end','YYYY-MM-DD HH24:mi.ss') ,'HH24:mi.ss'),'YYYY-MM-DD HH24:mi.ss' ) + (_diferent_time::text || ' day'::text)::interval ;





						return_record.title 		:= _calendar->>'title';

						return_record.start 		:= _time_start;

						return_record."end" 		:= _time_end;

						return_record.availability_id 	:= _availability.id;



						return_record.id := _availability.id::character varying

							|| '_'|| (_time_start::TIMESTAMP)::TEXT||(_time_end::TIMESTAMP)::TEXT;



						-- verificando que la disponibilidad no sea dinamica

						IF (_natural_time_start = _time_start AND _natural_time_end = _time_end)THEN

							return_record.d 	:= FALSE;

						ELSE

							return_record.d 	:= TRUE;

						END IF;



						--IF (_calendar::json->>'id' <> return_record.id) THEN

							return_record.id_ref := _calendar::json->>'id';

						--END IF;



						RETURN NEXT return_record;

					END IF;

				END LOOP;

			END IF;

		END LOOP;

	END LOOP;

END;

$$;


