--
-- Name: get_base_appointment(text, date, date); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.get_base_appointment(p_query text, p_start date, p_end date) RETURNS SETOF public.t_appointment
    LANGUAGE plpgsql
    AS $$
DECLARE

	return_record   		t_appointment%rowtype;
	_shadow				shadow_appointments%rowtype;
	_json				RECORD;
	_start				date	:=	p_start;
	_end				date	:=	p_end;

	_recurrencia			character varying[];
	_crear_dia			boolean	:= FALSE;
	_sequence			character varying;

	-- queries
	_query_shadow_appointment	character varying;
	_query_services			character varying := '';

BEGIN

	_query_shadow_appointment := p_query;

	FOR _shadow IN EXECUTE(_query_shadow_appointment) LOOP
		CASE _shadow.repeat::json->>'tipo'
			-- la recurrencia es 'none' por lo tanto no se usa
			WHEN 'dia' THEN

				IF(_shadow.repeat::json->>'recurrencia' = 'none')THEN

					return_record.id 	:= _shadow.calendar::json->>'id';
					return_record.title 	:= _shadow.calendar::json->>'title';
					return_record.start 	:= to_timestamp(_shadow.calendar::json->>'start','YYYY-MM-DD hh24:mi:ss');
					return_record."end" 	:= to_timestamp(_shadow.calendar::json->>'end','YYYY-MM-DD hh24:mi:ss');
					return_record.id_shadow := _shadow.id;

					_sequence := _shadow.id::character varying || '_'|| md5(return_record.start::character varying || return_record."end"::character varying);

					return_record.id = _sequence;

					-- los active posiblemente haya que quitarlos, ya que es un campo que no se deberia de mostrar en el calendario, mas si los estatus
					IF _shadow.active = TRUE THEN

						return_record.active 	:= 1;
					ELSE

						return_record.active 	:= 0;
					END IF;

					RETURN NEXT return_record;
				ELSE

					FOR _json IN EXECUTE('SELECT generate_series::DATE as time_dinamic FROM generate_series('''||p_start::DATE||'''::DATE,'''||p_end::DATE||'''::DATE,'''|| (_shadow.repeat::json->>'intervalo'::text)|| ' '||(_shadow.repeat::json->>'recurrencia') ||''')') LOOP
						return_record.id 	:= _shadow.calendar::json->>'id';
						return_record.title 	:= _shadow.calendar::json->>'title';

						-- Seteando horas
						--raise notice '%',TRIM(_json.time_dinamic::TEXT ||' '|| substring(_shadow.calendar::json->>'start'::TEXT,12,8));
						return_record.start 	:= TRIM(_json.time_dinamic::TEXT ||' '|| substring(_shadow.calendar::json->>'start'::TEXT,12,8));
						return_record."end" 	:= TRIM(_json.time_dinamic::TEXT ||' '|| substring(_shadow.calendar::json->>'end'::TEXT,12,8));

						_sequence := _shadow.id::character varying || '_'|| md5(return_record.start::character varying || return_record."end"::character varying);
						return_record.id = _sequence;
						return_record.id_shadow := _shadow.id;

						IF _shadow.active = TRUE THEN
							return_record.active 	:= 1;
						ELSE
							return_record.active 	:= 0;
						END IF;

						RETURN NEXT return_record;
					END LOOP;
				END IF;
			WHEN 'semana' THEN

				_start		:= p_start;

				FOR _json IN EXECUTE('SELECT generate_series::DATE as time_dinamic FROM generate_series('''||p_start::DATE||'''::DATE,'''||p_end::DATE||'''::DATE,'''|| (_shadow.repeat::json->>'intervalo'::text)|| ' '||(_shadow.repeat::json->>'recurrencia') ||''')') LOOP
					_start := _json.time_dinamic::date;

					_recurrencia := STRING_TO_ARRAY(_shadow.repeat::json->>'day_week',',');
					_crear_dia := FALSE;

					<<RECURRENCIA>>
					FOR i IN 1..(array_length(_recurrencia,1))LOOP

						CASE _recurrencia[i]
							WHEN 'domingo' THEN
								IF  to_char(_start,'d') = '1' THEN --DOMINGO
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							WHEN 'lunes' THEN
								IF  to_char(_start,'d') = '2' THEN --LUNES
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							WHEN 'martes' THEN
								IF  to_char(_start,'d') = '3' THEN --MARTES
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							WHEN 'miercoles' THEN
								IF  to_char(_start,'d') = '4' THEN --MIERCOLES
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							WHEN 'jueves' THEN
								IF  to_char(_start,'d') = '5' THEN --JUEVES
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							WHEN 'viernes' THEN
								IF  to_char(_start,'d') = '6' THEN --VIERNES
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							WHEN 'sabado' THEN
								IF  to_char(_start,'d') = '7' THEN --SABADO
									_crear_dia := TRUE;
									EXIT RECURRENCIA;
								END IF;
							ELSE
								NULL;
						END CASE;
					END LOOP RECURRENCIA;
					IF (_crear_dia)THEN
						return_record.id 	:= _shadow.calendar::json->>'id';
						return_record.title 	:= _shadow.calendar::json->>'title';
						return_record.start 	:= TRIM(_start::TEXT ||' '|| substring(_shadow.calendar::json->>'start'::TEXT,12,9));
						return_record."end" 	:= TRIM(_start::TEXT ||' '|| substring(_shadow.calendar::json->>'end'::TEXT,12,9));

						_sequence := _shadow.id::character varying || '_'|| md5(return_record.start::character varying || return_record."end"::character varying);
						return_record.id = _sequence;
						return_record.id_shadow := _shadow.id;

						IF _shadow.active = TRUE THEN
							return_record.active 	:= 1;
						ELSE
							return_record.active 	:= 0;
						END IF;
						RETURN NEXT return_record;
					END IF;
				END LOOP;
			WHEN 'mes' THEN
					_start	:= p_start;
					_recurrencia := STRING_TO_ARRAY(_shadow.repeat::json->>'day_month',',');

					FOR _json IN EXECUTE('SELECT generate_series::DATE as time_dinamic FROM generate_series('''||p_start::DATE||'''::DATE,'''||p_end::DATE||'''::DATE,'''|| (_shadow.repeat::json->>'intervalo'::text)|| ' day'')') LOOP
						_start := _json.time_dinamic::date;
							_crear_dia := FALSE;

							<<RECURRENCIA>>
							FOR i IN 1..(array_length(_recurrencia,1))LOOP

								CASE _recurrencia[i]
									WHEN 'primero' THEN

										IF  to_char(_start,'DD') = '01' THEN --PRIMERO

											_crear_dia := TRUE;
											EXIT RECURRENCIA;
										END IF;
									WHEN 'quince' THEN
										IF  to_char(_start,'DD') = '15' THEN --QUINCENA
											_crear_dia := TRUE;
											EXIT RECURRENCIA;
										END IF;
									WHEN 'ultimo' THEN			     --ULTIMO
										IF  to_char(_start,'DD') = TO_CHAR(((date_trunc('month', _start) + interval '1 month') - interval '1 day')::date,'DD') THEN --ULTIMO
											_crear_dia := TRUE;
											EXIT RECURRENCIA;
										END IF;
									ELSE
										NULL;
								END CASE;
							END LOOP RECURRENCIA;

							IF (_crear_dia)THEN
								return_record.id 	:= _shadow.calendar::json->>'id';
								return_record.title 	:= _shadow.calendar::json->>'title';
								return_record.start 	:= TRIM(_start::TEXT ||' '|| substring(_shadow.calendar::json->>'start'::TEXT,12,9));
								return_record."end" 	:= TRIM(_start::TEXT ||' '|| substring(_shadow.calendar::json->>'end'::TEXT,12,9));

								_sequence := _shadow.id::character varying || '_'|| md5(return_record.start::character varying || return_record."end"::character varying);
								return_record.id = _sequence;
								return_record.id_shadow := _shadow.id;

								IF _shadow.active = TRUE THEN
									return_record.active 	:= 1;
								ELSE
									return_record.active 	:= 0;
								END IF;
								 RETURN NEXT return_record;
							END IF;
					END LOOP;
			END CASE;
	END LOOP;

	-- SELECT to_char(now() + CAST('2 days' AS INTERVAL),'d');
	/*
		SELECT * FROM generate_series('2015-02-01 00:00'::timestamp,
                              '2015-03-28 12:00', '15 day');

	      SELECT * FROM generate_series('2015-02-01 00:00'::timestamp,
	      '2015-03-28 12:00', '15 week');

	       SELECT * FROM generate_series('2015-02-01 00:00'::timestamp,
	      '2015-03-28 12:00', '1 month');

	*/

	--RETURN;
END;
$$;


