--

CREATE FUNCTION public.get_contacts(p_lists json, p_contacts json, p_user_id integer, p_action integer, p_list_id integer) RETURNS SETOF public.t_contact
    LANGUAGE plpgsql
    AS $$

DECLARE

	_return_record	t_contact%rowtype;

	_r_contact	RECORD;

	_r_list		RECORD;

	_user_id	INTEGER;







BEGIN



	-- si el registro no se encuentra en la siguiente tabla, inmediatamente se retorna 0, ya que indica que el usuario no pertenece al sistema



	DROP TABLE IF EXISTS lists_temp;



	CREATE TEMPORARY TABLE lists_temp AS

		SELECT * from json_to_recordset(p_lists) as x (id integer, /*email character varying,*/ checked BOOLEAN)

	;



	DROP TABLE IF EXISTS contacts_temp;

	CREATE TEMPORARY TABLE contacts_temp AS

		SELECT * from json_to_recordset(p_contacts) as x (email character varying, checked boolean)

	;



	DROP TABLE IF EXISTS concat_contacts_temp;

	CREATE TEMPORARY TABLE concat_contacts_temp (

		email character varying,

		checked boolean

	);



	INSERT INTO concat_contacts_temp (email,checked)

		(SELECT email, checked FROM contacts_temp);





	IF p_action = 1 THEN

		FOR _r_contact IN (

			SELECT CASE WHEN col.email IS NOT NULL THEN

					col.email

				ELSE

					(SELECT u.email FROM users u WHERE u.id = col.user_id)

				END email

			FROM lists_temp lup

			JOIN list_users lu ON (lu.id = lup.id)

			JOIN contacts_of_list col ON (col.list_user_id = lu.id)

			WHERE lu.user_id = p_user_id AND lup.checked = TRUE

		) LOOP

			IF NOT EXISTS (SELECT 1 FROM concat_contacts_temp colt WHERE colt.email = _r_contact.email)

			THEN

				INSERT INTO concat_contacts_temp (

					email,

					checked

				) VALUES (

					_r_contact.email,

					false

				);

			END IF;

		END LOOP;

	ELSE



		DELETE FROM concat_contacts_temp



		WHERE email in (SELECT email FROM (

				SELECT 	CASE WHEN col.email IS NOT NULL THEN col.email

					ELSE (SELECT u.email FROM users u WHERE u.id = col.user_id)

					END email

				FROM list_users lu

				JOIN contacts_of_list col ON (col.list_user_id = lu.id)

				where col.list_user_id = p_list_id AND lu.user_id = p_user_id) AS emails

			WHERE emails.email not in (

				SELECT 	CASE WHEN col.email IS NOT NULL THEN col.email

					ELSE (SELECT u.email FROM users u WHERE u.id =col.user_id)

					END email

				FROM list_users lu

				JOIN contacts_of_list col ON (col.list_user_id = lu.id)

				WHERE col.list_user_id IN (SELECT id FROM lists_temp WHERE checked = true and id != p_list_id)

				AND lu.user_id = p_user_id

			)

		);



	END IF;





	FOR _r_contact IN (

		SELECT email, checked FROM concat_contacts_temp

	) LOOP

		_return_record.email := _r_contact.email;

		_return_record.checked := _r_contact.checked;



		RETURN NEXT _return_record;

	END LOOP;





END;

$$;


