BEGIN



	-- si el registro no se encuentra en la siguiente tabla, inmediatamente se retorna 0, ya que indica que el usuario no pertenece al sistema

	IF EXISTS (SELECT 1 FROM list_users lu WHERE lu.user_id = p_user_id) THEN



		DROP TABLE IF EXISTS contacts_temp;



		CREATE TEMPORARY TABLE contacts_temp AS

			SELECT * from json_to_recordset(p_contacts) as x (id INTEGER, checked BOOLEAN, "email" character varying)

		;



		FOR _r_list_users IN (SELECT * FROM list_users l WHERE l.user_id = p_user_id) LOOP



			_count_contacts := (SELECT COUNT(1) FROM contacts_of_list col

				WHERE col.list_user_id = _r_list_users.id);



			_count_contacts_temp := (SELECT COUNT(1) FROM contacts_temp col_temp

				WHERE col_temp.checked = true);



			_count_contacts_merge_temp   := (SELECT COUNT(1) FROM contacts_temp col_temp

							JOIN contacts_of_list col ON (col.list_user_id = _r_list_users.id

								AND col_temp.checked = TRUE

								AND (col.email = col_temp.email OR col.user_id = (SELECT u.id FROM users u WHERE u.email = col_temp.email))));





			_return_record.id 	:= _r_list_users.id;

			_return_record.name 	:= _r_list_users.name;



			raise notice 'nombre: %',_return_record.name;

			raise notice '_count_contacts: %',_count_contacts;

			raise notice '_count_contacts_temp: %',_count_contacts_temp;

			raise notice '_count_contacts_merge_temp: %',_count_contacts_merge_temp;

			raise notice '-------- 0 -------: %','';



			IF  _count_contacts = 0  THEN

				_return_record.checked 	:= 0;

				RETURN NEXT _return_record;

			ELSIF _count_contacts_temp = _count_contacts  AND _count_contacts = _count_contacts_merge_temp THEN

				_return_record.checked 	:= 1;

				RETURN NEXT _return_record;

			ELSE

				_return_record.checked 	:= 2;

				RETURN NEXT _return_record;

			END IF;



		END LOOP;



	END IF;



END;

$$;


