--

CREATE FUNCTION public.save_appointment(json_configuracion json, json_data json, p_user_id integer, p_service_id integer, p_emails_sep_comma character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $$

DECLARE



	return_record   	t_appointment%rowtype;

	_shadow			shadow_appointments%rowtype;

	_json_configuracion	json := json_configuracion;

	_json_data		json := json_data;

	_json			RECORD;

	_shadow_appointment_id	INTEGER;



BEGIN



	-- verificando si es realmente el usuario quien esta registrando la nueva disponibilidad

	IF NOT EXISTS (SELECT 1 FROM users_business ub

			JOIN services s ON s.busines_id = ub.business_id

			WHERE ub.user_id = p_user_id AND s.id = p_service_id) THEN

		RETURN 0;

	END IF;



	-- validando que la fecha de inicio de la cita no esté vencida o caducada

	IF (to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss') < NOW()) THEN

		RETURN 2; --No puede crear una cita con una fecha vencida o caducada

	END IF;



	/*

	IF NOT EXISTS (SELECT 1 FROM (

			SELECT * from get_availability(

				p_user_id,p_service_id,

				((_json_data::json->>'start')::date - interval '7 day')::DATE,

				((_json_data::json->>'end')::date + interval '7 day')::DATE

			)

			) as av WHERE av.start <= (_json_data::json->>'start')::TIMESTAMP AND av.end >= (_json_data::json->>'end')::TIMESTAMP) THEN

		RETURN 2;

	END IF;

	*/



	CASE _json_configuracion::json->>'tipo'



	WHEN 'dia' THEN



		IF EXISTS(

			SELECT 1 FROM shadow_appointments

			WHERE

			((time_start >= to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss')

				AND time_end <= to_timestamp(_json_data::json->>'end','YYYY-MM-DD hh24:mi:ss')

			) OR (

				time_start >= to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss')

				AND time_end is null

			)) AND service_id = p_service_id

		) THEN



			-- EXISTE

			RETURN 3;

		ELSE



			-- NO EXISTE

			_shadow_appointment_id := nextval('shadow_appointments_id_seq');

			_json_data := jsonb_set(_json_data::jsonb,'{id_shadow}',(_shadow_appointment_id)::character varying::jsonb,true);



			INSERT INTO shadow_appointments (

				id,

				service_id,

				state_shadow_appointment_id,

				repeat,

				calendar,

				type_shadow_appointment_id,

				time_start,

				time_end,

				active,

				title

			) VALUES (

				_shadow_appointment_id,

				p_service_id,

				1,

				_json_configuracion,

				_json_data,

				1,

				to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss'),

				to_timestamp(_json_data::json->>'end','YYYY-MM-DD hh24:mi:ss'),

				TRUE,

				(_json_data::json->>'title')::character varying

			);



			--raise notice '%','hola';



			-- envia una invitacion

			raise notice 'p_user_id-->%', p_user_id;

			raise notice '_shadow_appointment_id-->%',_shadow_appointment_id;

			raise notice 'p_emails_sep_comma-->%',p_emails_sep_comma;



			IF (TRIM(p_emails_sep_comma) <> '' || p_emails_sep_comma IS NOT NULL)THEN



				PERFORM save_invitation(p_user_id,_shadow_appointment_id,p_emails_sep_comma,1,null);



			END IF;





			RETURN 1;



		END IF;

	END CASE;



	RETURN 0;

END;

$$;


