--
-- Name: save_appointment_general(json, json, integer, integer, boolean, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.save_appointment_general(json_configuracion json, json_data json, p_user_id integer, p_service_id integer, p_is_alone_gift boolean, p_type integer) RETURNS json
    LANGUAGE plpgsql
    AS $$
DECLARE
	return_record   	t_appointment%rowtype;
	_shadow			shadow_appointments%rowtype;
	_json_configuracion	json := json_configuracion;
	_json_data		json := json_data;
	_json			RECORD;
	_row_user_bus		RECORD;
	_spa_tim		RECORD;
	_shadow_appointment_id	INTEGER;
	_invitations_id		INTEGER;

BEGIN


	-- Validando si los usuarios pueden agendar una cita en el servicio
	IF NOT EXISTS(SELECT * FROM services WHERE id = p_service_id AND is_scheduled IS TRUE)THEN
		RETURN '{"state":4}'::json; --No puede agendar una cita en el servicio seleccionado
	END IF;

	-- validando que la fecha de inicio de la cita no esté vencida o caducada
	IF (to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss') < NOW()) THEN
		RETURN '{"state":5}'::json; --No puede crear una cita con una fecha vencida o caducada.
	END IF;

	-- Validando que la cita sea creada con anticipacion con respecto aL tiempo configurado y la fecha actual
	SELECT value INTO _spa_tim FROM configuration_data WHERE code = 'spa_tim';
	IF (to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss') + _spa_tim.value::interval < NOW()) THEN
		RETURN '{"state":6}'::json; --La cita debe ser creada con anticipación.
	END IF;

	IF NOT EXISTS (SELECT 1
		FROM (
			SELECT * from get_availability_general(
				p_user_id,
				p_service_id,
				((_json_data::json->>'start')::date - interval '7 day')::DATE,
				((_json_data::json->>'end')::date + interval '7 day')::DATE,
				null
			)
		) as av WHERE av.start <= (_json_data::json->>'start')::TIMESTAMP AND av.end >= (_json_data::json->>'end')::TIMESTAMP) THEN
		RETURN '{"state":2}'::json; --No hoy rango de disponibilidad
	END IF;



	CASE _json_configuracion::json->>'tipo'

	WHEN 'dia' THEN

		IF EXISTS(
			SELECT 1 FROM shadow_appointments
			WHERE
			((time_start >= to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss')
				AND time_end <= to_timestamp(_json_data::json->>'end','YYYY-MM-DD hh24:mi:ss')
			) OR (
				time_start >= to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss')
				AND time_end is null
			)) AND service_id = p_service_id
		) THEN

			-- EXISTE
			RETURN '{"state":3}'::json;--La cita ya existe en el sistema
		ELSE
			-- NO EXISTE
			_shadow_appointment_id := nextval('shadow_appointments_id_seq');
			_json_data := jsonb_set(_json_data::jsonb,'{id_shadow}',(_shadow_appointment_id)::character varying::jsonb,true);

			--usuario crea la cita deacuerdo a la disponibilidad del negocio.';
			INSERT INTO shadow_appointments (
				id,
				service_id,
				repeat,
				calendar,
				time_start,
				time_end,
				active,
				title
			) VALUES (
				_shadow_appointment_id,
				p_service_id,
				_json_configuracion,
				_json_data,
				to_timestamp(_json_data::json->>'start','YYYY-MM-DD hh24:mi:ss'),
				to_timestamp(_json_data::json->>'end','YYYY-MM-DD hh24:mi:ss'),
				TRUE,
				(_json_data::json->>'title')::character varying
			);

			--Se obtiene el identificador del usuario dueño del negocio
			SELECT ub.user_id INTO _row_user_bus
			FROM shadow_appointments sa
			     JOIN services s ON s.id = sa.service_id
			     JOIN business b ON b.id = s.busines_id
			     JOIN users_business ub ON ub.business_id = b.id
			     JOIN public.user_roles ur ON(ur.id = ub.user_role_id AND ur.role_code = 'PAD')
			WHERE sa.id = _shadow_appointment_id;

			-- Se crea la atoinvitación del usuario una vez creada la cita deacuerdo a la disponibilidad del negocio.';
			IF(p_is_alone_gift IS FALSE OR (p_type = 2 AND p_is_alone_gift IS TRUE))THEN
				INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
				VALUES (p_user_id, p_user_id, _shadow_appointment_id, TRUE, 6);

				-- se crea una invitación hacia el usuario del negocio para notificarle que un usuario o cliente  ha creado una cita.
				IF NOT EXISTS(SELECT *
					FROM public.invitations i WHERE type_invitation IN (11) AND to_user_id = _row_user_bus.user_id AND shadow_id = _shadow_appointment_id)THEN
					RAISE NOTICE '-->%','NOTIFICACION AL NEGOCIO CUANDO EL USUARIO CREA LA CITA';

					INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
					VALUES (p_user_id, _row_user_bus.user_id, _shadow_appointment_id, TRUE, 11);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

					--Notificacion al negocio cuando un usuario o cliente ha creado una cita
					PERFORM save_invitation_notification(_invitations_id,10);

				END IF;
			END IF;

			RETURN (('{"state":1,"shadow_id":'|| _shadow_appointment_id ||'}')::text)::json;--Se ha registrado exitosamente la cita

		END IF;
	END CASE;

	RETURN '{"state":5}'::json;
END;
$$;


