					RAISE NOTICE '-->%','NOTIFICACION AL NEGOCIO CUANDO EL USUARIO CREA LA CITA';

					INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
					VALUES (p_user_id, _row_user_bus.user_id, _shadow_appointment_id, TRUE, 11);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

					--Notificacion al negocio cuando un usuario o cliente ha creado una cita
					PERFORM save_invitation_notification(_invitations_id,10);

				END IF;
			END IF;

			RETURN (('{"state":1,"shadow_id":'|| _shadow_appointment_id ||'}')::text)::json;--Se ha registrado exitosamente la cita

		END IF;
	END CASE;

	RETURN '{"state":5}'::json;
END;
$$;


