--

CREATE FUNCTION public.save_availability(p_user_id integer, p_service_id integer, p_availability_id integer, p_title character varying, p_calendar_json json) RETURNS integer
    LANGUAGE plpgsql
    AS $$

DECLARE



	_shadow			shadow_appointments%rowtype;

	_calendar		JSON;

	_calendar_availability	JSON;

	_calendar_out		JSON;

	_calendar_updated	JSON;

	_push_calendar 		JSON;

	_json			RECORD;

	_r_availability_table	RECORD;

	--_a_r_availability_in	RECORD[];

	_r_availability_in	RECORD;

	_update			BOOLEAN := FALSE;

	_delete			BOOLEAN := FALSE;

	_cont			INTEGER :=0;

	_size			INTEGER :=0;

	_id_availability	INTEGER;



	_availability		json;



BEGIN



	IF NOT EXISTS (

		SELECT 1 FROM services s

		JOIN business b ON s.busines_id = b.id

		JOIN users_business ub ON ub.business_id = b.id AND ub.user_id = p_user_id

		WHERE

		s.id = p_service_id

	) THEN

		-- se esta intentando actualizar un registro que no pertenece al usuario, puede ser considerado una intrucion maliciosa

		RETURN 3;

	END IF;



	IF (p_availability_id <> -1)THEN

		_id_availability := p_availability_id;

		_update := TRUE;

	-- esta condicion la estoy colocando asi mientras mas adelante se piensa como hacer la disponibilidad multiple por negocios, ya que por ahora solo se esta manejando una

	-- mas adelante se puede hacer para que el usuario pueda crear aparte la lista de disponibilidad y luego actualizar segun lo que se selecciona en el calendario de disponibilidad

	ELSIF (EXISTS (SELECT 1 FROM availabilitys WHERE service_id = p_service_id))THEN

		_id_availability := (SELECT id FROM availabilitys WHERE service_id = p_service_id);

		_update := TRUE;

	ELSE

		_update := FALSE;

	END IF;



	raise notice '%',p_availability_id;

	IF (_update = TRUE) THEN



		_availability := (SELECT av.calendar FROM availabilitys av

		JOIN services s ON s.id = av.service_id

		JOIN business b on (s.busines_id = b.id)

		JOIN users_business ub on (ub.user_id = p_user_id AND ub.business_id = b.id)

		JOIN users u on (ub.user_id = u.id)

		WHERE

		u.id = p_user_id

		AND s.id = p_service_id

		AND av.id = _id_availability);



		--_calendar_out := p_calendar_json;



		-- creando tabla de disponibilidad proveniente de parametros

		DROP TABLE IF EXISTS availability_table_temp;

		DROP TABLE IF EXISTS availability_in_temp;

		DROP TABLE IF EXISTS availability_in_update;



		CREATE TEMPORARY TABLE availability_in_temp AS

			select * from json_to_recordset(p_calendar_json) as x (id text,title character varying, "start" timestamp, "end" timestamp,d boolean, id_ref text, "action" text)

		;



		-- creando tabla de disponibilidad proveniente de la tabla

		CREATE TEMP TABLE availability_table_temp AS (

			select * from json_to_recordset(_availability) as x(id text,title character varying, "start" timestamp, "end" timestamp,d boolean, id_ref text, "erasable" boolean)

		);



		UPDATE availability_table_temp SET erasable = true;



		-- insertando disponibilidades nuevas, el atributo 'action' solo se dibuja cuando se va a insertar

		FOR _r_availability_in IN (SELECT * FROM availability_in_temp) LOOP

			IF (_r_availability_in.action = 'I') THEN

				INSERT INTO availability_table_temp (

					id,

					title,

					"start",

					"end",

					d,

					id_ref,

					"erasable"

				) VALUES (

					_id_availability::TEXT || '_' || (_r_availability_in.start::TEXT || _r_availability_in.end::TEXT),

					_r_availability_in."title",

					_r_availability_in."start",

					_r_availability_in."end",

					false,

					null,

					false

				);

			ELSE

				UPDATE availability_table_temp

					SET

					"title" = _r_availability_in.title,

					"start" = _r_availability_in.start,

					"end" 	= _r_availability_in."end"

					WHERE id = _r_availability_in.id_ref

				;

			END IF;

		END LOOP;



		--  borrando disponibilidades solo si el registro original de la disponibilidad no conjuga con el de entrada

		FOR _r_availability_table IN (SELECT * FROM availability_table_temp att WHERE att.erasable = TRUE) LOOP



			IF NOT EXISTS (SELECT 1 FROM availability_in_temp ait

				WHERE ait.id_ref = _r_availability_table.id

			) THEN

				DELETE FROM availability_table_temp WHERE id = _r_availability_table.id;

			ELSE



			END IF;



		END LOOP;



		_size := (SELECT COUNT(1) FROM JSON_ARRAY_ELEMENTS(_availability));



		--_calendar_out :=_availability;

		_calendar_out :=  (SELECT array_to_json(array_agg(row_to_json(x))) FROM (SELECT att.id, att.title, att.start, att.end, att.d, att.id_ref FROM availability_table_temp att) x);





		UPDATE availabilitys SET

			title 		= p_title,

			calendar 	= _calendar_out

		WHERE

			ID = _id_availability

			AND service_id = p_service_id

		;

	ELSE

		_id_availability := NEXTVAL('availabilitys_id_seq');



		INSERT INTO availabilitys(

			id,

			title,

			calendar,

			service_id

		) VALUES (

			_id_availability,

			p_title,

			p_calendar_json,

			p_service_id

		);

	END IF;



	RETURN 1;

END;

$$;


