CREATE FUNCTION public.save_contact(p_txt_email character varying, p_list_user_id integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	_row		RECORD;
	_user_id 	INTEGER;
	_email 		CHARACTER VARYING;

BEGIN

	IF EXISTS (SELECT 1 FROM contacts_of_list lc
			LEFT JOIN users u ON (lc.user_id = u.id)
			WHERE list_user_id = p_list_user_id AND (lc.email = p_txt_email OR u.email = p_txt_email) LIMIT 1) THEN

		RETURN 2;--Existe un contacto registrado con el mismo correo.
	ELSE
		SELECT id INTO _row FROM users WHERE email = p_txt_email;

		IF (_row.id IS NOT NULL ) THEN
			_user_id  = _row.id;
			_email 	  = null;
		ELSE
			_user_id  = null;
			_email 	  = p_txt_email;
		END IF;

		INSERT INTO contacts_of_list(
			list_user_id,
			user_id,
			email
		) VALUES (
			p_list_user_id,
			_user_id,
			_email
		);
		RETURN 1;--se registro satisfactoriamente
	END IF;

END;
$$;


