CREATE FUNCTION public.save_contacts_of_list(p_group_inv_id integer, p_checked boolean, p_contacts_id json, p_user_id integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$

DECLARE

	_r_contacts	RECORD;

	_user_id	INTEGER;

	_FIRTS		BOOLEAN := TRUE;

BEGIN



	-- si el registro no se encuentra en la siguiente tabla, inmediatamente se retorna 0, ya que indica que el usuario no pertenece al sistema

	IF NOT EXISTS (SELECT 1 FROM list_users lu WHERE lu.id = p_group_inv_id AND user_id = p_user_id) THEN

		RETURN 0;

	END IF;



	DROP TABLE IF EXISTS user_contacts_temp;



--	select * from user_contacts_temp;



	CREATE TEMPORARY TABLE user_contacts_temp AS

		select * from json_to_recordset(p_contacts_id) as x (id INTEGER, checked BOOLEAN, "email" character varying)

	;



	IF p_checked = TRUE THEN



		FOR _r_contacts IN (SELECT * FROM user_contacts_temp) LOOP



			_user_id := (SELECT id FROM users u WHERE u.email = _r_contacts.email);



			IF (_r_contacts.checked = TRUE) THEN

				IF (_FIRTS = TRUE) THEN

					DELETE FROM contacts_of_list

					WHERE list_user_id = p_group_inv_id;

					_FIRTS := FALSE;

				END IF;



				IF _user_id IS NULL THEN



					raise notice '%','1111';

					--IF NOT EXISTS (SELECT 1 FROM contacts_of_list col WHERE col.list_user_id = p_group_inv_id AND col.email = _r_contacts.email) THEN



						INSERT INTO contacts_of_list (

							list_user_id,

							email

						) VALUES (

							p_group_inv_id,

							_r_contacts.email

						);



					--END IF;

				ELSE



					raise notice '%','2222';

					--IF NOT EXISTS (SELECT 1 FROM contacts_of_list col WHERE col.list_user_id = p_group_inv_id AND col.user_id = _user_id) THEN



						INSERT INTO contacts_of_list (

							list_user_id,

							user_id

						) VALUES (

							p_group_inv_id,

							_user_id

						);

					--END IF;

				END IF;



			ELSE

				-- eliminando uno por uno a los contactos

				IF _user_id IS NULL THEN

					DELETE FROM contacts_of_list

					WHERE list_user_id = p_group_inv_id

					AND email = _r_contacts.email;

				ELSE

					DELETE FROM contacts_of_list

					WHERE list_user_id = p_group_inv_id

					AND user_id = _user_id;

				END IF;



			END IF;



		END LOOP;



	ELSIF p_checked = FALSE THEN

		-- eliminando toda los contactos al deseleccionar la lista

		FOR _r_contacts IN (SELECT * FROM user_contacts_temp uct WHERE uct.checked = TRUE) LOOP

			_user_id := (SELECT id FROM users u WHERE u.email = _r_contacts.email);



			IF _user_id IS NULL THEN

				DELETE FROM contacts_of_list

				WHERE list_user_id = p_group_inv_id

				AND email = _r_contacts.email;

			ELSE

				DELETE FROM contacts_of_list

				WHERE list_user_id = p_group_inv_id

				AND user_id = _user_id;

			END IF;

		END LOOP;



	END IF;



	RETURN 1;



END;

$$;


