	_last_name		= _dato[3];
        _password		= _dato[4];

	-- se verifica si existe el token del usuario socio invitado que se está registrando
	IF EXISTS (SELECT 1 FROM public.invitations i WHERE trim(token) = trim(_token) LIMIT 1) THEN
		--se obtiene la informacion de la invitacion del usuario amigo
		SELECT id as id_invitation, from_user_id, to_email INTO _row
		FROM public.invitations i WHERE trim(token) = trim(_token);

