			username
		) VALUES (
			_row.to_email,
			_password,
			_first_name,
			_last_name,
			_row.to_email
		);
		_users_id = currval('users_id_seq');

		UPDATE public.invitations
		SET 	to_user_id = _users_id,
			token = null,
			to_email = null,
			is_user_system = true
		WHERE id = _row.id_invitation;
		/*
		-- Si existe el usuario que invitó, se inserta la asociación del usuario amigo con el cliente
		IF EXISTS(SELECT * FROM public.users WHERE id = _row.from_user_id)THEN

			--Acepta la invitación
			PERFORM public.accept_invitation(ARRAY[_users_id::TEXT,_row.id_invitation::TEXT]);
		END IF;
		*/

		RETURN _users_id;--se retorna el ID del usuario amigo si el registro es satisfactoriamente


	ELSE
		RETURN 2;--No existe el token de invitacion del usuario amigo
	END IF;

END;
$_$;


