CREATE FUNCTION public.save_guest_user(character varying[]) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
DECLARE

	_dato		 	ALIAS FOR $1;
	_token			CHARACTER VARYING;
	_first_name		CHARACTER VARYING;
	_last_name		CHARACTER VARYING;
        _password		CHARACTER VARYING;

        _row			RECORD;
        _users_id		INTEGER;

BEGIN
	_token			= _dato[1];
	_first_name		= _dato[2];
	_last_name		= _dato[3];
        _password		= _dato[4];

	-- se verifica si existe el token del usuario invitado que se está registrando
	IF EXISTS (SELECT 1 FROM public.invitations i WHERE trim(token) = trim(_token) LIMIT 1) THEN

		--se obtiene la informacion de la invitacion del usuario O cliente
		SELECT i.id as id_invitation, i.to_email, s.busines_id as id_business, code INTO _row
		FROM public.invitations i
		JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
		JOIN public.services s ON(s.id = sa.service_id)
		JOIN business b ON (b.id = s.busines_id)
		JOIN notifications.notification_invitations ni ON (ni.invitation_id = i.id)
		JOIN notifications.notifications n ON (n.id = ni.notification_id AND n.dinamic = true)
		WHERE trim(token) = trim(_token);

		INSERT INTO public.users(
			email,
			password,
			first_name,
			last_name,
			username
		) VALUES (
			_row.to_email,
			_password,
			_first_name,
			_last_name,
			_row.to_email
		);
		_users_id = currval('users_id_seq');

		UPDATE public.invitations
		SET 	to_user_id = _users_id,
			token = null,
			to_email = null,
			is_user_system = true
		WHERE id = _row.id_invitation;


		-- Si existe el negocio al que fue invitado el cliente, se inserta la asociación del cliente con el negocio aceptando la invitación
		/*
		IF (_row.id_business IS NOT NULL) THEN
			--Acepta la invitación
			PERFORM public.accept_invitation(ARRAY[_users_id::TEXT,_row.id_invitation::TEXT]);
		END IF;
		*/

		RETURN _users_id;--se retorna el ID del usuario si el registro es satisfactoriamente
	ELSE
		RETURN 2;--No existe el token de invitacion del usuario

	END IF;

END;
$_$;


