CREATE FUNCTION public.save_invitation(p_user_id integer, p_generic_id integer, p_email_clients character varying, p_type_invitation integer, p_aditional_param json) RETURNS integer
    LANGUAGE plpgsql
    AS $$

DECLARE

	_email			CHARACTER VARYING;
	_client_id		INTEGER;
	_row 			RECORD;
	_row_inv		RECORD;
	_row_clients		RECORD;
	_invitations_id		INTEGER;

BEGIN

	-- Se obtiene el negocio que invita

	SELECT s.busines_id INTO _row
	FROM shadow_appointments sa JOIN public.services s ON(s.id = sa.service_id)
	WHERE sa.id = p_generic_id;

	--Primero se recorre todos los clientes del negocio para el envio de la notificación cuando se crea la cita
	FOR _row_clients IN (SELECT * FROM public.clients_business cb WHERE cb.business_id = _row.busines_id) LOOP

		--Se valida que no exista una notificacion con la misma cita enviada por el usuario que envia la notificacion
		IF NOT EXISTS(SELECT 1
				FROM notifications.notification_invitations ni
				JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = 1)
				JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
				JOIN public.services s ON(s.id = sa.service_id)
				WHERE ni.send_dinamic = TRUE AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'NOTCIT')
				AND i.to_user_id = _row_clients.user_id AND s.busines_id = _row.busines_id
				AND sa.id = p_generic_id
		) THEN

			INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation, aditional_param)
			VALUES (p_user_id, _row_clients.user_id, p_generic_id, TRUE, p_type_invitation, p_aditional_param);

			_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;
			PERFORM save_invitation_notification(_invitations_id,1);

		END IF;
	END LOOP;

	--Luego se recorre todos los correos de los usuarios invitados por el negocio cuando se crea la cita
	FOREACH _email IN ARRAY STRING_TO_ARRAY(p_email_clients,';') LOOP

		_client_id := (SELECT u.id FROM users u WHERE u.email = _email);

		IF (_email <> '') THEN

			-- si el usuario existe no se registra el token
			IF (_client_id IS NOT NULL)THEN
				--RAISE NOTICE 'A-->%',_client_id;
				--RAISE NOTICE '%','Usuario SI existe en el sistema->'||p_type_invitation;
				--Si la invitación es por cita
				CASE p_type_invitation
				WHEN 1 THEN

					--SELECT * FROM users u WHERE u.email = 'coquito@gmail.com'
					--Se valida que no exista una invitacion del negocio hacia el cliente
					SELECT i.id AS invitation_id INTO _row_inv
					FROM invitations i
					JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
					JOIN public.services s ON(s.id = sa.service_id)
					JOIN clients_business cb ON (cb.user_id = i.to_user_id AND cb.business_id = s.busines_id)
					WHERE s.busines_id = _row.busines_id
					AND i.to_user_id = _client_id
					AND type_invitation = p_type_invitation LIMIT 1;


					IF (_row_inv.invitation_id IS NULL)THEN
						--RAISE NOTICE '%','No existe la invitacion';

						-- si no existe la invitacion se crea la invitacion
						INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation, aditional_param)
						VALUES (p_user_id, _client_id, p_generic_id, TRUE, p_type_invitation, p_aditional_param);

						_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

						--Si el usuario invitado no es cliente del negocio que lo invita, estonces inserto la notificacion para la invitacion del cliente
						IF NOT EXISTS(SELECT 1 FROM public.clients_business cb WHERE cb.user_id = _client_id AND cb.business_id = _row.busines_id)THEN

							--Si no existe una notificacion de invitación del usuario invitado y el negocio que lo invita
							IF NOT EXISTS (SELECT 1
								FROM notifications.notification_invitations ni
								JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = 1)
								JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
								JOIN public.services s ON(s.id = sa.service_id)
								WHERE ni.send_dinamic = TRUE
								AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'INVCLI')
								AND i.to_user_id = _client_id AND s.busines_id = _row.busines_id
							) THEN

								PERFORM save_invitation_notification(_invitations_id,6);

							END IF;

						-- Si el usuario invitado es cliente, entonces inserto la notificacion
						ELSE
							--RAISE NOTICE '%','El usuario invitado es cliente por lo tanto se envia la notificacion';
							PERFORM save_invitation_notification(_invitations_id,1);
						END IF;
					END IF;
				--Si la invitación es de socio
				WHEN 2 THEN

					INSERT INTO invitations(from_user_id, to_user_id, business_id, is_user_system, type_invitation, aditional_param)
					VALUES (p_user_id, _client_id, p_generic_id, TRUE, p_type_invitation, p_aditional_param);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;
					PERFORM save_invitation_notification(_invitations_id,2);

				END CASE;
			ELSE
				--RAISE NOTICE 'B-->%',_client_id;
				--Si la invitación es por cita
				CASE p_type_invitation
				WHEN 1 THEN
					INSERT INTO invitations( token, from_user_id, to_email, shadow_id, is_user_system, type_invitation, aditional_param)
					VALUES (md5(now()::text || _email|| p_generic_id), p_user_id, trim(_email), p_generic_id, FALSE, p_type_invitation, p_aditional_param);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

					IF NOT EXISTS (SELECT 1
						FROM notifications.notification_invitations ni
						JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = 1)
						JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
						JOIN public.services s ON(s.id = sa.service_id)
						WHERE ni.send_dinamic = FALSE AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'INVCLI')
						AND s.busines_id = _row.busines_id AND i.to_email = _email
					) THEN

						PERFORM save_invitation_notification(_invitations_id,6);
					END IF;
				--Si la invitación es de socio que no existe en el sistema
				WHEN 2 THEN
					INSERT INTO invitations( token, from_user_id, to_email, business_id, is_user_system, type_invitation, aditional_param)
					VALUES (md5(now()::text || _email|| p_generic_id), p_user_id, trim(_email), p_generic_id, FALSE, p_type_invitation, p_aditional_param);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;
					PERFORM save_invitation_notification(_invitations_id,2);
				END CASE;


			END IF;
		END IF;
	END LOOP;

	RETURN 1;

END;

$$;


