--
-- Name: save_invitation_friend(integer, integer, character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.save_invitation_friend(p_user_id integer, p_shadow_appointment_id integer, p_email_friends character varying) RETURNS json
    LANGUAGE plpgsql
    AS $$
DECLARE
	_email			CHARACTER VARYING;
	_friend_id		INTEGER;
	_row 			RECORD;
	_row_inv		RECORD;
	_row_friends		RECORD;
	_invitations_id		INTEGER;
	_type_invitation 	INTEGER = 3;
	_arr_emails		CHARACTER VARYING[];
	_result_json		JSON;
BEGIN


	IF(p_email_friends = '')THEN
		_result_json = json_build_object(
			'value',4,
			'message','Ups!, debes seleccionar a un usuario para invitar'
		);
		RETURN _result_json;
	END IF;

	_arr_emails = string_to_array(p_email_friends,';');

	--Se valida que no exista una invitacion al usuario invitado con el mismo usuario que lo invitó
	SELECT array_to_string(array_agg(a.email), ','::text) AS emails INTO _row_inv
	FROM(   SELECT CASE WHEN i.to_user_id IS NOT NULL THEN u.email ELSE i.to_email
		END AS email
		FROM invitations i
		JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
		LEFT JOIN users u ON (u.id = i.to_user_id)
		WHERE i.from_user_id = p_user_id AND shadow_id = p_shadow_appointment_id AND type_invitation = _type_invitation
	)AS a
	WHERE a.email = ANY(_arr_emails::text[]);

	IF(_row_inv.emails IS NOT NULL)THEN
		_result_json = json_build_object(
			'value',3,
			'message','Ups!, ya has realizado la invitación a los siguientes usuarios',
			'additional_data',_row_inv.emails
		);
		RETURN _result_json;
	END IF;


	-- Se obtiene el negocio dueño de la cita
	SELECT s.busines_id INTO _row
	FROM shadow_appointments sa JOIN public.services s ON(s.id = sa.service_id)
	WHERE sa.id = p_shadow_appointment_id;


	--Se verifica si el usuario que invita es cliente del negocio que creo la cita, es decir, si es cliente puede invitar a sus amigos a la cita
	IF EXISTS(SELECT 1 FROM public.clients_business cb WHERE cb.user_id = p_user_id AND cb.business_id = _row.busines_id) THEN

		--Primero se recorre todos los amigos del usuario que invita para el envio de la notificación de la cita
		FOR _row_friends IN (SELECT * FROM public.friends f WHERE f.from_user_id = p_user_id) LOOP

			--Se valida que no exista una notificacion con la misma cita enviada por el usuario que envia la notificacion
			IF NOT EXISTS(SELECT i.id AS invitation_id FROM invitations i
					JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
					JOIN notifications.notification_invitations ni ON(ni.invitation_id = i.id)
					JOIN notifications.notifications n ON (n.id = ni.notification_id)
					WHERE i.from_user_id = p_user_id
					AND i.to_user_id = _row_friends.to_user_id
					AND shadow_id = p_shadow_appointment_id
					AND type_invitation = _type_invitation
					AND n.code = 'NOTCITAMI') THEN

				RAISE NOTICE '%','INVITA';

				INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
				VALUES (p_user_id, _row_friends.to_user_id, p_shadow_appointment_id, TRUE, _type_invitation);

				_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

				PERFORM save_invitation_notification(_invitations_id,4);
			END IF;
		END LOOP;
	ELSE
		_result_json = json_build_object(
			'value',2,
			'message','Ups! no tienes permiso para invitar, ya que no eres cliente del negocio'
		);
		RETURN _result_json;
	END IF;



	--Luego se recorre todos los correos de los amigos invitados por el usuario
	FOREACH _email IN ARRAY STRING_TO_ARRAY(p_email_friends,';') LOOP

		IF (_email <> '') THEN
			_friend_id := (SELECT u.id FROM users u WHERE u.email = _email);

			-- si el usuario existe no se registra el token
			IF (_friend_id IS NOT NULL)THEN
				RAISE NOTICE '%','Usuario SI existe en el sistema';

				--Si el usuario invitado no es amigo del usuario que lo invita, estonces inserto la notificacion para la invitacion del usuario
				IF NOT EXISTS(SELECT 1 FROM public.friends f WHERE f.from_user_id = p_user_id AND f.to_user_id = _friend_id)THEN
					RAISE NOTICE '%','No es amigo del usuario que lo invita';
					--Si no existe una notificacion de invitación del usuario invitado y el usuario que lo invita

					IF NOT EXISTS (SELECT 1 FROM notifications.notification_invitations ni
						JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = _type_invitation)
						JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
						WHERE ni.send_dinamic = TRUE
						AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'INVAMI')
						AND i.from_user_id = p_user_id AND i.to_user_id = _friend_id) THEN

						INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
						VALUES (p_user_id, _friend_id, p_shadow_appointment_id, TRUE, _type_invitation);

						_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

						RAISE NOTICE '%','No existe la notification_invitations se crea';

						PERFORM save_invitation_notification(_invitations_id,3);

					END IF;

				END IF;

			ELSE
				RAISE NOTICE '%','Usuario invitado NO existe en el sistema';

				IF NOT EXISTS (SELECT * FROM notifications.notification_invitations ni
					JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = _type_invitation)
					JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
					JOIN public.services s ON(s.id = sa.service_id)
					WHERE ni.send_dinamic = FALSE AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'INVAMI')
					AND i.from_user_id = p_user_id AND i.to_email = _email) THEN

					INSERT INTO invitations( token, from_user_id, to_email, shadow_id, is_user_system, type_invitation)
					VALUES (md5(now()::text || _email|| p_shadow_appointment_id), p_user_id, trim(_email), p_shadow_appointment_id, FALSE, _type_invitation);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

					PERFORM save_invitation_notification(_invitations_id,3);

				END IF;

			END IF;
		END IF;

	END LOOP;

	_result_json = json_build_object(
		'value',1,
		'message','se registro satisfactoriamente.'
	);
	RETURN _result_json;

END;
$$;


