				AND n.code = 'NOTCITAMI') THEN

			RAISE NOTICE '%','INVITA';

			INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
			VALUES (p_user_id, _row_friends.to_user_id, p_shadow_appointment_id, TRUE, _type_invitation);

			_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

			PERFORM save_invitation_notification(_invitations_id,4);
		END IF;
	END LOOP;


	--Luego se recorre todos los correos de los amigos invitados por el usuario
	FOREACH _email IN ARRAY STRING_TO_ARRAY(p_email_friends,';') LOOP

		IF (_email <> '') THEN
			_friend_id := (SELECT u.id FROM users u WHERE u.email = _email);

			-- si el usuario existe no se registra el token
			IF (_friend_id IS NOT NULL)THEN
				RAISE NOTICE '%','Usuario SI existe en el sistema';

				--Si el usuario invitado no es amigo del usuario que lo invita, estonces inserto la notificacion para la invitacion del usuario
				IF NOT EXISTS(SELECT 1 FROM public.friends f WHERE f.from_user_id = p_user_id AND f.to_user_id = _friend_id)THEN
					RAISE NOTICE '%','No es amigo del usuario que lo invita';
					--Si no existe una notificacion de invitación del usuario invitado y el usuario que lo invita

					IF NOT EXISTS (SELECT 1 FROM notifications.notification_invitations ni
						JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = _type_invitation)
						JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
						WHERE ni.send_dinamic = TRUE
						AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'INVAMI')
						AND i.from_user_id = p_user_id AND i.to_user_id = _friend_id) THEN

						INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation)
						VALUES (p_user_id, _friend_id, p_shadow_appointment_id, TRUE, _type_invitation);

						_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

						RAISE NOTICE '%','No existe la notification_invitations se crea';

						PERFORM save_invitation_notification(_invitations_id,3);

					END IF;

				END IF;

			ELSE
				RAISE NOTICE '%','Usuario invitado NO existe en el sistema';

				IF NOT EXISTS (SELECT * FROM notifications.notification_invitations ni
					JOIN invitations i ON (i.id = ni.invitation_id AND type_invitation = _type_invitation)
					JOIN shadow_appointments sa ON (sa.id = i.shadow_id)
					JOIN public.services s ON(s.id = sa.service_id)
					WHERE ni.send_dinamic = FALSE AND ni.notification_id = (SELECT id FROM notifications.notifications n WHERE n.code = 'INVAMI')
					AND i.from_user_id = p_user_id AND i.to_email = _email) THEN

					INSERT INTO invitations( token, from_user_id, to_email, shadow_id, is_user_system, type_invitation)
					VALUES (md5(now()::text || _email|| p_shadow_appointment_id), p_user_id, trim(_email), p_shadow_appointment_id, FALSE, _type_invitation);

					_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

					PERFORM save_invitation_notification(_invitations_id,3);

				END IF;

			END IF;
		END IF;

	END LOOP;

	_result_json = json_build_object(
		'value',1,
		'message','se registro satisfactoriamente.'
	);
	RETURN _result_json;

END;
$$;


