-- Name: save_invitation_notification(integer, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.save_invitation_notification(p_invitation_id integer, p_type_notification integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE

	_notification_code CHARACTER VARYING;

BEGIN

	CASE p_type_notification
	WHEN 1 THEN --Invitación de un negocio a un cliente o notificación al crear la cita
		_notification_code = 'NOTCIT';
	WHEN 2 THEN --Invitación de un negocio a un socio
		_notification_code = 'INVSOC';
	WHEN 3 THEN --Invitación de amigos
		_notification_code = 'INVAMI';
	WHEN 4 THEN --Notificación de invitación a la cita enviada por el amigo
		_notification_code = 'NOTCITAMI';
	WHEN 5 THEN --Notificación de regalo a la cita enviada por un usuario
		_notification_code = 'REGCIT';
	WHEN 6 THEN --Notificación de regalo a la cita enviada por un usuario
		_notification_code = 'INVCLI';
	WHEN 7 THEN --Notificación enviada por correo al usuario que compra la cita
		_notification_code = 'COMCIT';
	WHEN 8 THEN --Notificación enviada por correo al negocio notificando que un usuario se ha hecho cliente
		_notification_code = 'NOTCLIBUS';
	WHEN 9 THEN --Notificación al negocio informando que un usuario o cliente  ha comprado una cita.
		_notification_code = 'NOTCOMCIT';
	WHEN 10 THEN --Notificacion al negocio informando que un usuario o cliente ha creado una cita deacuerdo a su disponibiliad.
		_notification_code = 'NOTCRECIT';
	WHEN 11 THEN --Notificación al negocio informando que un usuario o cliente  ha comprado y regalado una cita.
		_notification_code = 'NOTCYRCIT';
	WHEN 12 THEN --Notificación al negocio informando que un usuario o cliente  ha regalado una cita.
		_notification_code = 'NOTREGCIT';
	ELSE
		RETURN 0;
	END CASE;

	raise notice '_notification_code-->%',_notification_code;

	INSERT INTO notifications.notification_invitations (
		invitation_id,
		notification_id
	) VALUES (
		p_invitation_id,
		(SELECT id FROM notifications.notifications n WHERE n.code = _notification_code)
	);

	RETURN 1;

END;
$$;


