--

CREATE FUNCTION public.save_invitation_partner(character varying[]) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
DECLARE

	_dato		 	ALIAS FOR $1;
	_p_email		CHARACTER VARYING;
	_p_role_code		CHARACTER VARYING;
	_p_list_users_ids	CHARACTER VARYING;
	_p_business_ids		CHARACTER VARYING;
	_p_user_log		INTEGER;

	_r			RECORD;
	_row			RECORD;
	_user_id 		INTEGER;
	_email 			CHARACTER VARYING;
	_business_id		INTEGER;

	_arr_list_users_ids	INTEGER[];
	_aditional_param	JSON;

BEGIN

	_p_email		= _dato[1];
	_p_role_code		= _dato[2];
	_p_business_ids		= _dato[3];
	_p_list_users_ids	= _dato[4];
	_p_user_log		= _dato[5];


	/*Si no existe una lista de contactos se crea una lista por defecto para añadir al socio en esa lista,
	sino se añade en las listas de contactos seleccionada por el usuario*/
	IF NOT EXISTS (SELECT 1 FROM public.list_users WHERE user_id = _p_user_log) THEN
		INSERT INTO public.list_users(name,user_id) VALUES ('Mis socios',_p_user_log);
		_arr_list_users_ids = string_to_array(''||currval('list_users_id_seq')||'', ',');
	ELSE
		_arr_list_users_ids = string_to_array(''||_p_list_users_ids||'', ',');
	END IF;



	-- Se verifica si el usuario socio existe o no en el sistema
	SELECT id INTO _row FROM users WHERE email = _p_email;
	IF (_row.id IS NOT NULL ) THEN
		_user_id  = _row.id;
		_email 	  = null;
	ELSE
		_user_id  = null;
		_email 	  = _p_email;
	END IF;


	--RAISE NOTICE '%',_arr_list_users_ids;
	--Se recorre las listas de contactos
	FOR i IN 1..(array_length(_arr_list_users_ids,1))
	LOOP

		--Si no existe el socio en las listas de contactos, se inserta en la lista de contactos
		IF NOT EXISTS (	SELECT 1
				FROM public.contacts_of_list lc
				LEFT JOIN public.users u ON (lc.user_id = u.id)
				WHERE list_user_id =  _arr_list_users_ids[i] AND (lc.email = _p_email OR u.email = _p_email) LIMIT 1) THEN


			INSERT INTO public.contacts_of_list(
				list_user_id,
				user_id,
				email
			) VALUES (
				_arr_list_users_ids[i],
				_user_id,
				_email
			);
		END IF;

	END LOOP;

	--Se agrega como parametro adicional el rol del usuario socio
	_aditional_param = json_build_object('role_code',_p_role_code);
	---raise notice '-->>%',_aditional_param;

	--Se recorre los Ids de los negocios, para invitar al socio por cada negocio seleccionado
	FOREACH _business_id IN ARRAY STRING_TO_ARRAY(_p_business_ids,',')
	LOOP

		IF NOT EXISTS (	SELECT *
				FROM(
					SELECT 	CASE WHEN is_user_system IS TRUE THEN
						(select email from public.users where id = i.to_user_id) ELSE i.to_email END  email_partner,
						i.business_id as business_id,is_user_system,type_invitation
					FROM public.invitations i
					WHERE type_invitation = 2
				) AS a WHERE email_partner = _p_email AND business_id = _business_id LIMIT 1
		) THEN

			RAISE NOTICE '_aditional_param-->%',_aditional_param;
			PERFORM save_invitation(_p_user_log,_business_id,_p_email,2,_aditional_param);
		ELSE
			RETURN 0;-- Ya existe la invitación del socio
		END IF;
	END LOOP;

	RETURN 1;



END;
$_$;


