CREATE FUNCTION public.save_notification(p_to_user_id bigint, p_from_user_id bigint, p_type_notification integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE

	_notification_code 		CHARACTER VARYING;
	_user_type			INTEGER;

BEGIN

	CASE p_type_notification

	WHEN 1 THEN --Notificación enviada por correo al negocio notificando que un usuario se ha hecho cliente
		_notification_code = 'NOTCLIBUS'; --tn9
		_user_type := 0;
	WHEN 2 THEN --Notificación al negocio informando que un usuario o cliente  ha comprado una cita.
		_notification_code = 'NOTCOMCIT'; --tn10
		_user_type := 0;
	WHEN 3 THEN --Notificacion al negocio informando que un usuario o cliente ha creado una cita deacuerdo a su disponibiliad.
		_notification_code = 'NOTCRECIT'; --tn11
		_user_type := 0;
	WHEN 4 THEN --Notificación al negocio informando que un usuario o cliente  ha comprado y regalado una cita.
		_notification_code = 'NOTCYRCIT';--t 10
		_user_type := 0;
	WHEN 5 THEN --Notificación al negocio informando que un usuario o cliente  ha regalado una cita.
		_notification_code = 'NOTREGCIT';--t 12
		_user_type := 0;
	ELSE
		RETURN 0;
	END CASE;

	raise notice '_notification_code-->%',_notification_code;

	INSERT INTO notifications.notificacion_users (
		notification_id,
		from_user_id,
		to_user_id,
		user_type
	) VALUES (
		(SELECT id FROM notifications.notifications n WHERE n.code = _notification_code),
		p_from_user_id,
		p_to_user_id,
		_user_type
	);

	RETURN 1;

END;
