--
-- Name: save_pay_personal(character varying[]); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.save_pay_personal(character varying[]) RETURNS json
    LANGUAGE plpgsql
    AS $_$
DECLARE
	_dato				ALIAS FOR $1;
	p_user_id			INTEGER;
	p_shadow_id			INTEGER;
	p_emails_users_give_away	CHARACTER VARYING;
	p_is_alone_gift 		BOOLEAN;

	_quantity			INTEGER;
	_quantity_to_give_away		INTEGER;
	_price				DOUBLE PRECISION;

	_row_conf			RECORD;
	_row_shadow			RECORD;
	_row_inv			RECORD;
	_row_user_bus			RECORD;

	_total_price_to_give_away	DOUBLE PRECISION;
	_total_tax_price		DOUBLE PRECISION;
	_total_price_commission		DOUBLE PRECISION;
	_total_amount_paid		DOUBLE PRECISION;

	_type_invitation 		INTEGER = 4;
	_paid_out_clients_id		INTEGER;
	_users_give_away_id		INTEGER;
	_invitations_id			INTEGER;
	_card_id			INTEGER;
	_email				CHARACTER VARYING;
	_response			json;
	_result_json			JSON;

	_receipt_code_me		CHARACTER VARYING:=NULL;
	_receipt_gift_code		JSON[];
	_receipt_id			bigint;
	_t_receipt			receipt_payments%rowtype;
	_t_receipt_gif			receipt_payments%rowtype;
BEGIN
	p_user_id			= _dato[1];
	p_shadow_id			= _dato[2];
	p_emails_users_give_away	= _dato[3];
	p_is_alone_gift			= _dato[4];
	_card_id			= _dato[5];
	_response			= _dato[6];

	--Se obtiene los porcentajes de comision y impuesto
	SELECT (array_agg(value))[1] AS tax_per,(array_agg(value))[2] as com_per INTO _row_conf
	FROM public.configuration_data WHERE code IN ('tax_per','com_per');

	--Obtiene el precio del servicio
	SELECT s.price, time_start, time_end,s.busines_id INTO _row_shadow
	FROM shadow_appointments sa
	JOIN services s ON (s.id = sa.service_id)
	WHERE sa.id = p_shadow_id;

	--Se obtiene el usuario de la empresa o negocio
	SELECT ub.user_id INTO _row_user_bus
	FROM public.users_business ub
	JOIN public.user_roles ur ON(ur.id = ub.user_role_id)
	WHERE role_code = 'PAD' AND business_id = _row_shadow.busines_id;


	--Cantidad de citas a regalar
	_quantity_to_give_away = coalesce(array_length(string_to_array(p_emails_users_give_away,','),1),'0');

	_price = (_row_shadow.price)::DOUBLE PRECISION;
	IF(_quantity_to_give_away > 0)THEN
		IF(p_is_alone_gift IS TRUE)THEN

			_quantity = _quantity_to_give_away;
			_total_price_to_give_away = (_price * _quantity_to_give_away)::DOUBLE PRECISION;
			--_total_tax_price = (( _total_price_to_give_away) * (_row_conf.tax_per)::INTEGER/100)::DOUBLE PRECISION;
			_total_tax_price = 0;
			_total_price_commission = ((_total_price_to_give_away) * (_row_conf.com_per)::INTEGER/100)::DOUBLE PRECISION;

		ELSE

			_quantity = _quantity_to_give_away + 1;
			_total_price_to_give_away = (_price * _quantity_to_give_away)::DOUBLE PRECISION;
			--_total_tax_price = ((_price + _total_price_to_give_away) * (_row_conf.tax_per)::INTEGER/100)::DOUBLE PRECISION;
			_total_tax_price = 0;
			_total_price_commission = ((_price + _total_price_to_give_away) * (_row_conf.com_per)::INTEGER/100)::DOUBLE PRECISION;

		END IF;

	ELSE

		_quantity = 1;
		_total_price_to_give_away = 0;
                --_total_tax_price = (_price * (_row_conf.tax_per)::INTEGER/100)::DOUBLE PRECISION;
                _total_tax_price = 0;
                _total_price_commission = (_price * (_row_conf.com_per)::INTEGER/100)::DOUBLE PRECISION;

	END IF;

	-- es solo regalo
	IF(p_is_alone_gift IS TRUE)THEN

		_total_amount_paid = (_total_price_to_give_away +_total_tax_price + _total_price_commission)::DOUBLE PRECISION;

	ELSE

		_total_amount_paid = (_price + _total_price_to_give_away +_total_tax_price + _total_price_commission)::DOUBLE PRECISION;

	END IF;


	-- Se inserta el pago realizado por el cliente
	INSERT INTO public.paid_out_clients(
		shadow_id,
		user_id,
		quantity,
		price,
		tax_percentage,
		commission_percentage,
		total_amount_paid,
		response_service,
		card_id

	) VALUES (
		p_shadow_id,
		p_user_id,
		_quantity,
		round(_price::NUMERIC,2),
		--_row_conf.tax_per::INTEGER,
		0,
		_row_conf.com_per::INTEGER,
		round(_total_amount_paid::NUMERIC,2),
		_response,
		_card_id::INTEGER
	);

	_paid_out_clients_id = (SELECT CURRVAL('paid_out_clients_id_seq'))::INTEGER;

	-- es solo regalo? es auto invitacion, aqui solo se paga la autoinvitacion del usuario
	IF(p_is_alone_gift IS FALSE)THEN
		--RAISE NOTICE 'p_shadow_id-->%',p_shadow_id;
		-- validando si es una autoinvitacion, SI
		IF NOT EXISTS(SELECT *
			FROM public.invitations i WHERE type_invitation IN (1,5) AND to_user_id = p_user_id AND shadow_id = p_shadow_id)THEN
			--RAISE NOTICE '-->%','NOO EXISTE LA INVITACION';

			INSERT INTO receipt_payments (created_at) values (NOW());

			_receipt_id := CURRVAL('seq_receipt_payments'::regclass);
			SELECT * into _t_receipt FROM receipt_payments WHERE id = _receipt_id;
			_receipt_code_me := _t_receipt.random_code;

			INSERT INTO invitations
			(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation, paid_out_clients_id,receipt_payment_id)
			VALUES (p_user_id, p_user_id, p_shadow_id, TRUE, 5, _paid_out_clients_id,_receipt_id);

			RAISE NOTICE '-->%','1';

			_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

			-- Se envia por correo la informacion al usuario que compra la cita
			PERFORM save_invitation_notification(_invitations_id,7);

		-- si es una invitacion ya existente del negocio
		ELSE
			-- Se consulta la invitación del cliente para colocarla como pagada y crear la notificación para que se envie por correo
			--RAISE NOTICE '-->%','EXISTE LA INVITACION';
			--SELECT id FROM public.invitations i WHERE type_invitation IN (1,5) AND to_user_id = 113 AND shadow_id = 1752 ;
			SELECT id INTO _row_inv FROM public.invitations i WHERE type_invitation IN (1,5) AND to_user_id = p_user_id AND shadow_id = p_shadow_id ;

			INSERT INTO receipt_payments (created_at) values (NOW());

			_receipt_id := CURRVAL('seq_receipt_payments'::regclass);
			SELECT * into _t_receipt FROM receipt_payments WHERE id = _receipt_id;
			_receipt_code_me := _t_receipt.random_code;

			-- Luego se coloca como pagada la invitacion de la cita al usuario cliente
			UPDATE public.invitations
			SET
				paid_out_clients_id = _paid_out_clients_id,
				receipt_payment_id = _receipt_id
			WHERE id = _row_inv.id;

			-- Se envia por correo la informacion al usuario que compra la cita
			PERFORM save_invitation_notification(_row_inv.id,7);

		END IF;


		-- SOLO NOTIFICACION AL NEGOCIO
		-- se crea una invitación hacia el usuario del negocio para notificarle que un usuario o cliente  ha comprado una cita.
		IF NOT EXISTS(SELECT *
			FROM public.invitations i WHERE type_invitation IN (10) AND to_user_id = _row_user_bus.user_id AND shadow_id = p_shadow_id)THEN
			--RAISE NOTICE '-->%','NOTIFICACION AL NEGOCIO CUANDO EL USUARIO COMPRA LA CITA';

			-- colocar aqui logica de generacion de recibo de pago

			INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation, paid_out_clients_id)
			VALUES (p_user_id, _row_user_bus.user_id, p_shadow_id, TRUE, 10, _paid_out_clients_id);

			_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

			IF(_quantity_to_give_away > 0)THEN
				--Notificacion al negocio cuando un usuario o cliente ha comprado y regalado una cita
				PERFORM save_invitation_notification(_invitations_id,11);
			ELSE 	--Notificacion al negocio cuando un usuario o cliente ha comprado una cita
				PERFORM save_invitation_notification(_invitations_id,9);
			END IF;
		END IF;
	ELSE
		-- SOLO NOTIFICACION AL NEGOCIO
		-- se crea una invitación hacia el usuario del negocio para notificarle que un usuario o cliente  ha regalado una cita.
		IF NOT EXISTS(SELECT *
			FROM public.invitations i WHERE type_invitation IN (12) AND to_user_id = _row_user_bus.user_id AND shadow_id = p_shadow_id)THEN
			--RAISE NOTICE '-->%','NOTIFICACION AL NEGOCIO CUANDO EL USUARIO SOLO REGALA LA CITA';

			IF(_quantity_to_give_away > 0)THEN

				INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation, paid_out_clients_id)
				VALUES (p_user_id, _row_user_bus.user_id, p_shadow_id, TRUE, 12, _paid_out_clients_id);

				_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;

				--Notificacion al negocio cuando un usuario o cliente ha regalado una cita
				PERFORM save_invitation_notification(_invitations_id,12);
			END IF;
		END IF;
	END IF;



	--Si el cliente regala la cita a otros usuarios, se recorre todos los correos para crear la invitacion de regalo y pagarla
	FOREACH _email IN ARRAY STRING_TO_ARRAY(p_emails_users_give_away,',') LOOP
		--RAISE NOTICE '%', 'Gestiona los regalos';
		--RAISE NOTICE '_email%',_email;

		_users_give_away_id := (SELECT u.id FROM users u WHERE u.email = _email);

		--LOGICA PARA USUARIOS QUE SON PARTE DEL SISTEMA: cuando el usuario existe en el sistema. Si el usuario a regalar existe, no se registra el token
		IF (_users_give_away_id IS NOT NULL)THEN
			--RAISE NOTICE '%','Usuario SI existe en el sistema';

			-- si no existe una invitación que interfiera con el horario de la cita, se crea la invitacion y la notificacion de regalo.
			IF NOT EXISTS (SELECT 1 FROM (
				SELECT  (SELECT email FROM users u WHERE u.id = i.to_user_id ) AS emails
				FROM invitations i
				JOIN shadow_appointments sa ON(sa.id = i.shadow_id)
				WHERE i.paid_out_clients_id IS NOT NULL
				AND (sa.time_start::timestamp,  sa.time_end::timestamp) OVERLAPS (_row_shadow.time_start::timestamp, _row_shadow.time_end::timestamp)
				AND i.type_invitation NOT IN (9,10,11,12)
				) AS sq WHERE sq.emails = _email
			) THEN
				--RAISE NOTICE '%','No existe la invitación que interfiera para el usuario de sistema, por lo tanto se crea la invitacion y la notificacion de regalo';
				INSERT INTO receipt_payments (created_at) values (NOW());

				_receipt_id := CURRVAL('seq_receipt_payments'::regclass);
				SELECT * into _t_receipt FROM receipt_payments WHERE id = _receipt_id;

				_receipt_gift_code := array_append(_receipt_gift_code,
					json_build_object(
						'user',(SELECT '('|| first_name || ') ' || email FROM users u WHERE u.id = _users_give_away_id)::CHARACTER VARYING,
						'receipt',_t_receipt.random_code,
						'registered',true
					)
				);

				INSERT INTO invitations(from_user_id, to_user_id, shadow_id, is_user_system, type_invitation, paid_out_clients_id,receipt_payment_id)
				VALUES (p_user_id, _users_give_away_id, p_shadow_id, TRUE, _type_invitation, _paid_out_clients_id,_receipt_id);

				_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;
				PERFORM save_invitation_notification(_invitations_id,5);

			END IF;
		-- si el usuario no existe en el sistema se crea el token
		ELSE
			RAISE NOTICE '%','Usuario NO existe en el sistema';

			-- si no existe la invitacion que interfiera con el horario de la cita para el usuario a regalar, se crea la invitacion y la notificacion de regalo.
			IF NOT EXISTS (
				SELECT *
				FROM invitations i
					JOIN shadow_appointments sa ON(sa.id = i.shadow_id)
					JOIN notifications.notification_invitations ni  ON (ni.invitation_id = i.id AND ni.send_dinamic = FALSE )
				WHERE i.paid_out_clients_id IS NOT NULL
				AND (sa.time_start::timestamp,  sa.time_end::timestamp) OVERLAPS (_row_shadow.time_start::timestamp, _row_shadow.time_end::timestamp)
				AND i.to_email = _email
				AND i.type_invitation NOT IN (9,10,11,12)
			)THEN
				 RAISE NOTICE '%','No existe la invitación para el usuario nuevo, por lo tanto se crea la invitacion y la notificacion de regalo';

				INSERT INTO receipt_payments (created_at) values (NOW());

				_receipt_id := CURRVAL('seq_receipt_payments'::regclass);
				SELECT * into _t_receipt FROM receipt_payments WHERE id = _receipt_id;


				_receipt_gift_code := array_append(_receipt_gift_code,
					json_build_object(
						'user',_email,
						'receipt',_t_receipt.random_code,
						'registered',false
					)
				);

				INSERT INTO invitations( token, from_user_id, to_email, shadow_id, is_user_system, type_invitation, paid_out_clients_id,receipt_payment_id)
				VALUES (md5(now()::text || _email|| p_shadow_id), p_user_id, trim(_email), p_shadow_id, FALSE, _type_invitation, _paid_out_clients_id,_receipt_id);

				_invitations_id = (SELECT CURRVAL('invitations_id_seq'))::INTEGER;
				PERFORM save_invitation_notification(_invitations_id,5);

			END IF;

		END IF;
	END LOOP;

	_result_json = json_build_object(
		'status'		,1,
		'message'		,'Guardado correctamente',
		'receipt_code_me'	,_receipt_code_me,
		'receipt_code_gifts'	,array_to_json(_receipt_gift_code::jsonb[])
	);

	RETURN _result_json;

END;
$_$;


