CREATE FUNCTION public.save_service(character varying[]) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
DECLARE

	_dato		 	ALIAS FOR $1;
	_active			BOOLEAN;
	_title			CHARACTER VARYING;
	_description		CHARACTER VARYING;
	_terms			TEXT;
        _address		CHARACTER VARYING;
        _price			DOUBLE PRECISION;
        _limit_persons		INTEGER;
        _duration		INTERVAL;
        _busines_id		INTEGER;
        _category_id		INTEGER;
        _image			JSON;
        _is_scheduled		BOOLEAN;

        _service_id		INTEGER;
        _parish_id		INTEGER;

BEGIN
	_active			= _dato[1];
	_title			= _dato[2];
	_description		= _dato[3];

	IF(_dato[4] != '')THEN
		_terms		= _dato[4];
	ELSE
		_terms		= null;
	END IF;

	IF(_dato[5] != '')THEN
		_address	= _dato[5];
	ELSE
		_address	= null;
	END IF;

        _price			= _dato[6];

        IF(_dato[7] != '')THEN
		_limit_persons	= _dato[7];
	ELSE
		_limit_persons	= null;
	END IF;

        _duration		= _dato[8];
        _busines_id		= _dato[9];
        _category_id		= _dato[10];

	IF(_dato[11] != '{}')THEN
		_image	= _dato[11];
	ELSE
		_image	= null;
	END IF;

	_is_scheduled		= _dato[12];
	_parish_id		= _dato[13];


	IF NOT EXISTS(SELECT * FROM public.services WHERE title = _title AND busines_id = _busines_id) THEN

		INSERT INTO public.services(
			active,
			title,
			description,
			terms,
			address,
			price,
			limit_persons,
			duration,
			busines_id,
			category_id,
			image,
			is_scheduled,
			parish_id
		) VALUES (
			_active,
			_title,
			_description,
			_terms,
			_address,
			_price,
			_limit_persons,
			_duration,
			_busines_id,
			_category_id,
			_image,
			_is_scheduled,
			_parish_id
		);

		_service_id = currval('services_id_seq');

		RETURN _service_id;--se registro satisfactoriamente

	ELSE
		RETURN 0;--Existe un  servicio con el mismo titulo para la empresa
	END IF;


END;
$_$;


