CREATE FUNCTION utils.format_code(p_code bigint) RETURNS text
    LANGUAGE plpgsql
    AS $$



DECLARE

	_code			CHARACTER VARYING[];

	_code_generated		TEXT;

	_code_to_tranform	TEXT := p_code::CHARACTER VARYING;

	_length_code		integer := character_length(_code_to_tranform);

	_index			integer:=0;



BEGIN



	FOR i in 1.._length_code LOOP

		_index := ((_length_code + 1) -i);

		_code[_index] := substring(_code_to_tranform from _index for 1);



		IF (MOD(i,3) = 0 AND _length_code <> i) THEN

			_code[_index] := '-'||_code[_index];

		END IF;

	END LOOP;



	RETURN ARRAY_TO_STRING(_code,'');



END;

$$;


