CREATE VIEW notifications.view_all_buy_to_send_by_mail AS
 SELECT ni.id AS n_invitation_id,
    sa.title AS appointment_title,
    (sa.time_start)::date AS appointment_start_date,
    (sa.time_start)::time without time zone AS appointment_start_time,
    c.iso,
    n.email AS is_email,
    n.dinamic AS is_dinamic,
    ub.user_id,
    m.message_short,
    m.message_long,
    b.name_business,
        CASE
            WHEN (i.to_user_id IS NULL) THEN i.to_email
            ELSE ( SELECT ut.email
               FROM public.users ut
              WHERE (ut.id = i.to_user_id))
        END AS to_email,
    ( SELECT (((users.first_name)::text || ' '::text) || (users.last_name)::text)
           FROM public.users
          WHERE (users.id = i.from_user_id)) AS from_user_name,
    i.from_user_id,
    i.to_user_id,
    i.token,
    COALESCE(u.first_name, ' '::character varying) AS first_name,
    i.is_user_system,
    n.code,
    to_char(sa.time_start, 'DD/MM/YYYY HH12:MI:SS AM'::text) AS start_date_appointment,
    to_char(sa.time_end, 'DD/MM/YYYY HH12:MI:SS AM'::text) AS end_date_appointment,
    s.title AS title_serv
   FROM (((((((((notifications.notifications n
     JOIN notifications.messages m ON ((n.message_id = m.id)))
     JOIN word.countries c ON ((c.id = m.country_id)))
     JOIN notifications.notification_invitations ni ON (((ni.notification_id = n.id) AND (ni.send = false))))
     JOIN public.invitations i ON ((i.id = ni.invitation_id)))
     JOIN public.shadow_appointments sa ON ((sa.id = i.shadow_id)))
     JOIN public.services s ON ((s.id = sa.service_id)))
     JOIN public.users_business ub ON ((ub.business_id = s.busines_id)))
     JOIN public.business b ON ((b.id = ub.business_id)))
     LEFT JOIN public.users u ON ((u.id = i.to_user_id)))
