--
-- Name: view_all_notifications; Type: VIEW; Schema: notifications; Owner: -
--

CREATE VIEW notifications.view_all_notifications AS
 SELECT
        CASE
            WHEN (((n.code)::text = 'NOTCLIBUS'::text) OR ((n.code)::text = 'NOTCOMCIT'::text) OR ((n.code)::text = 'NOTCRECIT'::text) OR ((n.code)::text = 'NOTCYRCIT'::text) OR ((n.code)::text = 'NOTREGCIT'::text)) THEN i.from_user_id
            ELSE s.id
        END AS from_id,
        CASE
            WHEN (((n.code)::text = 'NOTCLIBUS'::text) OR ((n.code)::text = 'NOTCOMCIT'::text) OR ((n.code)::text = 'NOTCRECIT'::text) OR ((n.code)::text = 'NOTCYRCIT'::text) OR ((n.code)::text = 'NOTREGCIT'::text)) THEN ( SELECT users.user_image
               FROM public.users
              WHERE (users.id = i.from_user_id))
            ELSE s.image
        END AS from_image,
    true AS is_client,
    row_number() OVER (PARTITION BY i.to_user_id ORDER BY i.created_at DESC) AS row_number,
    i.id AS invitations_id,
    i.created_at,
    i.from_user_id,
    ( SELECT (((users.first_name)::text || ' '::text) || (users.last_name)::text)
           FROM public.users
          WHERE (users.id = i.from_user_id)) AS from_user_name,
    i.to_user_id,
    COALESCE((((u.first_name)::text || ' '::text) || (u.last_name)::text), ' '::text) AS to_user_name,
        CASE
            WHEN (i.to_user_id IS NOT NULL) THEN u.email
            ELSE i.to_email
        END AS to_email,
    i.shadow_id,
    s.busines_id AS business_id,
    sa.title,
    to_char(sa.time_start, 'DD/MM/YYYY HH12:MI:SS AM'::text) AS start_date_appointment,
    to_char(sa.time_end, 'DD/MM/YYYY HH12:MI:SS AM'::text) AS end_date_appointment,
    to_char((date_part('epoch'::text, (now() - (sa.created_at)::timestamp with time zone)) * '00:00:01'::interval), 'HH24h MIm SSs'::text) AS time_elapsed,
    b.name_business,
    b.url_profile_image,
    ni.id AS n_invitation_id,
    ni.invitation_id,
    ni.send_dinamic,
    utils.replace_title_inbox(m.message_short, s.title, 'SA'::character varying) AS message_short,
    m.message_long,
    ni.viewed,
    n.code,
    sa.service_id,
    s.title AS title_serv,
    s.image AS image_serv,
    ni.id AS notifications_invitation_id
   FROM (((((((notifications.notification_invitations ni
     JOIN public.invitations i ON ((i.id = ni.invitation_id)))
     JOIN public.shadow_appointments sa ON ((sa.id = i.shadow_id)))
     JOIN public.services s ON ((s.id = sa.service_id)))
     JOIN public.business b ON ((b.id = s.busines_id)))
     JOIN notifications.notifications n ON (((n.id = ni.notification_id) AND (n.dinamic = true))))
     JOIN notifications.messages m ON ((m.id = n.message_id)))
     LEFT JOIN public.users u ON ((u.id = i.to_user_id)))
  WHERE ((i.type_invitation = ANY (ARRAY[1, 3, 7, 9, 10, 11, 12])) AND ((n.code)::text = ANY (ARRAY['NOTCIT'::text, 'NOTCITAMI'::text, 'NOTCLIBUS'::text, 'NOTCOMCIT'::text, 'NOTCRECIT'::text, 'NOTCYRCIT'::text, 'NOTREGCIT'::text])) AND (i.is_user_system IS TRUE));


