--

CREATE VIEW public.view_all_appointments AS
 SELECT sa.id AS appointment_id,
    sa.created_at,
    s.busines_id,
        CASE
            WHEN ((now())::timestamp without time zone < sa.time_start) THEN true
            ELSE false
        END AS vigente,
    (sa.time_start)::date AS start_date_default,
    (sa.time_end)::date AS end_date_default,
    to_char(sa.time_start, 'DD/MM/YYYY'::text) AS start_date,
    to_char(sa.time_end, 'DD/MM/YYYY'::text) AS end_date,
    to_char(sa.time_start, 'HH12:MI:SS AM'::text) AS start_time,
    to_char(sa.time_end, 'HH12:MI:SS AM'::text) AS end_time,
    to_char(sa.time_start, 'DD/MM/YYYY HH12:MI:SS AM'::text) AS start_date_appointment,
    to_char(sa.time_end, 'DD/MM/YYYY HH12:MI:SS AM'::text) AS end_date_appointment,
    sa.service_id,
    sa.code AS code_appointment,
    s.title AS title_serv,
    s.description AS description_serv,
    s.terms AS terms_serv,
    s.address AS address_serv,
    s.price AS price_serv,
    s.limit_persons AS limit_persons_serv,
    s.duration AS duration_serv,
    s.busines_id AS busines_id_serv,
    s.category_id AS category_id_serv,
    s.image AS image_serv,
    s.active AS service_active,
    b.name_business,
    b.email AS email_business,
    b.phone AS phone_business,
    st.id AS state_id,
    st.name AS name_state,
    m.id AS municipality_id,
    m.name AS name_municipality,
    p.id AS parish_id,
    p.name AS name_parish,
    co.id AS country_id,
    co.name AS name_country
   FROM (((((((public.shadow_appointments sa
     JOIN public.services s ON ((sa.service_id = s.id)))
     JOIN public.business b ON ((s.busines_id = b.id)))
     JOIN public.category c ON ((s.category_id = c.id)))
     JOIN word.parishes p ON ((p.id = s.parish_id)))
     JOIN word.municipalities m ON ((m.id = p.municipality_id)))
     JOIN word.states st ON ((st.id = m.state_id)))
     JOIN word.countries co ON ((co.id = st.country_id)))
  ORDER BY sa.time_start DESC;


