     JOIN public.business b ON ((b.id = s.busines_id)))
     JOIN public.users_business ub ON ((ub.business_id = b.id)))
     JOIN public.user_roles ur ON (((ur.id = ub.user_role_id) AND ((ur.role_code)::text = 'PAD'::text))))
