--
-- Name: view_invitations_to_clients _RETURN; Type: RULE; Schema: notifications; Owner: -
--

CREATE RULE "_RETURN" AS
    ON SELECT TO notifications.view_invitations_to_clients DO INSTEAD  SELECT
        CASE
            WHEN (cb.id IS NOT NULL) THEN true
            ELSE false
        END AS invitation_accepted,
    i.id AS invitation_id,
    i.from_user_id,
    ( SELECT (((users.first_name)::text || ' '::text) || (users.last_name)::text)
           FROM public.users
          WHERE (users.id = i.from_user_id)) AS from_user_name,
    i.to_user_id,
    COALESCE((((u.first_name)::text || ' '::text) || (u.last_name)::text), ' '::text) AS to_user_name,
        CASE
            WHEN (i.to_user_id IS NOT NULL) THEN u.email
            ELSE i.to_email
        END AS to_email,
    i.shadow_id,
    b.id AS business_id,
    b.name_business,
    b.id AS from_id,
    b.business_image AS from_image,
    b.business_image,
    m.message_short,
    m.message_long,
    ni.viewed,
    ni.id AS n_invitation_id,
    ni.send_dinamic,
    ni.send,
    n.code,
    n.email AS is_email,
    n.dinamic AS is_dinamic,
    i.created_at,
    to_char((date_part('epoch'::text, (now() - (i.created_at)::timestamp with time zone)) * '00:00:01'::interval), 'HH24h MIm SSs'::text) AS time_elapsed,
    i.aditional_param,
    i.is_user_system,
    i.token,
    i.type_invitation,
    c.iso
   FROM (((((((((public.invitations i
     JOIN public.shadow_appointments sa ON ((sa.id = i.shadow_id)))
     JOIN public.services s ON ((s.id = sa.service_id)))
     JOIN public.business b ON ((b.id = s.busines_id)))
     JOIN notifications.notification_invitations ni ON ((ni.invitation_id = i.id)))
     JOIN notifications.notifications n ON (((n.id = ni.notification_id) AND (n.dinamic = true) AND ((n.code)::text = 'INVCLI'::text))))
     JOIN notifications.messages m ON ((m.id = n.message_id)))
     JOIN word.countries c ON ((c.id = m.country_id)))
     LEFT JOIN public.users u ON ((u.id = i.to_user_id)))
     LEFT JOIN public.clients_business cb ON (((cb.business_id = b.id) AND (cb.user_id = i.to_user_id))))
  WHERE (i.type_invitation = 1)
  GROUP BY i.id, cb.id, i.from_user_id, i.to_user_id, b.id, b.name_business, m.message_short, m.message_long, ni.viewed, ni.id, ni.send, ni.send_dinamic, n.code, i.created_at, i.token, n.email, n.dinamic, i.to_email, i.shadow_id, u.first_name, u.last_name, c.iso, u.email;


